-- ----------------------------
-- Table structure for think_account
-- ----------------------------
DROP TABLE IF EXISTS `think_account`;
CREATE TABLE `think_account` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `username` varchar(50) NOT NULL COMMENT '微信用户名呢称',
  `fid` int(10) unsigned NOT NULL COMMENT '是否是号主 0 表示客服微信号, 其他表示客户微信号',
  `mobile` bigint(11) DEFAULT '0' COMMENT '手机',
  `wechat_name` varchar(50) DEFAULT '' COMMENT '微信号',
  `c_type` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '客户类型 分A B C D 几个等级',
  `account_time` datetime DEFAULT NULL COMMENT '微信号的注册时间',
  `email` varchar(50) DEFAULT '' COMMENT '邮箱或QQ等联系方式',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '用户状态  1 正常  2 被封号',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `username` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COMMENT='微信用户表';

