-- ----------------------------
-- Table structure for think_article
-- ----------------------------
DROP TABLE IF EXISTS `think_article`;
CREATE TABLE `think_article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '文章ID',
  `cid` smallint(5) unsigned NOT NULL COMMENT '分类ID',
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `introduction` varchar(255) DEFAULT '' COMMENT '简介',
  `content` longtext COMMENT '内容',
  `author` varchar(20) DEFAULT '' COMMENT '作者',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 待审核  1 审核',
  `reading` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '阅读量',
  `thumb` varchar(255) DEFAULT '' COMMENT '缩略图',
  `photo` text COMMENT '图集',
  `is_top` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶  0 不置顶  1 置顶',
  `sort` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  `publish_time` datetime NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=12 DEFAULT CHARSET=utf8 COMMENT='文章表';

-- ----------------------------
-- Records of think_article
-- ----------------------------
INSERT INTO `think_article` VALUES ('10', '1', '尚庐山,凝梦想,尚凝集团高层游庐山', '庐山，又名匡山、匡庐，是中华十大名山、世界文化遗产、国家AAAAA级旅游景区、中国四大避暑胜地。地处江西省九江市庐山市境内，山体呈椭圆形，典型的地垒式块段山。', '<p>&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;庐山，又名匡山、匡庐，是中华十大名山、世界文化遗产、国家AAAAA级旅游景区、中国四大避暑胜地。地处江西省九江市庐山市境内，山体呈椭圆形，典型的地垒式块段山。</p><p><img src=\" /vipimg/510227.jpg\" title=\"510227.jpg\" alt=\"timg (7).jpg\" style=\"width: 680px; height: 181px;\" width=\"680\" height=\"181\"/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;庐山以雄、奇、险、秀闻名于世，具有极高的科学价值和旅游观赏价值，素有“匡庐奇秀甲天下”之美誉，与鸡公山、北戴河、莫干山并称“中国四大避暑胜地”。<br/><br/>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 庐山入选为：世界文化遗产、世界地质公园、全国重点文物保护单位、国家重点风景名胜区、国家5A级旅游景区、中华十大名山、中国最美十大名山、中国四大避暑胜地、首批全国文明风景旅游区示范点等荣誉称号。<br/><br/>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; 含鄱口、三叠泉、芦林湖、五老峰、黄龙潭、好汉坡、三宝树等均为庐山著名的风景点</p><p><img src=\"http://tp5.wechattpadmin.sn/vipimg/135937.jpg\" title=\"135937.jpg\" alt=\"timg (5).jpg\" style=\"width: 673px; height: 197px;\" width=\"673\" height=\"197\"/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;此次游庐山正是尚凝文化正式成立前夕, 一来希望作为尚凝集团的先遣军尚凝文化可以像庐山一样巍峨壮大,一来在庐山之巅许下对集团发展的美好愿景.</p><p>&nbsp;&nbsp;&nbsp;&nbsp;<img src=\" /vipimg/232156.jpg\" title=\"232156.jpg\" alt=\"微信图片_20170628201411.jpg\" style=\"width: 651px; height: 265px;\" width=\"651\" height=\"265\"/></p><p><br/></p><p style=\"text-align:center\"><img src=\" /vipimg/534885.jpg\" title=\"534885.jpg\" alt=\"微信图片_20170628201359.jpg\" style=\"width: 682px; height: 480px;\" width=\"682\" height=\"480\"/></p><p><br/></p><p>&nbsp;&nbsp;&nbsp;&nbsp;此致希望尚凝集团发展越来越好,&nbsp; 集团越来越壮大.<br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p>', '小尚', '1', '0', '/uploads/20170628/a44a7782462830635aa3c3473d034e2f.jpg', null, '0', '0', '2017-06-28 20:20:39', '2017-05-26 19:59:44');
INSERT INTO `think_article` VALUES ('11', '1', '尚凝夏旅,聚沙成塔', '2017年6月23日-24日广州尚凝文化有限公司集体游珠江', '<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;外伶仃岛在珠海香洲东南部。位于珠海市区27.5海里，北距深圳35海里左右，是珠三角地区进出南太平洋国际航线的必经之地，具有重要的战略地位。名字来历：因该岛伶仃孤立，且在外伶仃，故名外伶仃岛。 特征：地势东西高，北和中间低，东部沿岸较陡，倾斜角为35度。岛中央主峰伶仃峰高311．8米，天晴时可看到香港、桂山等岛及各岛沿线以内整个海面。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"text-align: center;\"><img src=\" /vipimg/116994.jpg\" title=\"116994.jpg\" alt=\"sea.jpg\" style=\"width: 675px; height: 295px;\" width=\"675\" height=\"295\"/></p><p><span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 28px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 28px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">外伶仃岛在珠海香洲东南部。\r\n \r\n特征:地势东西高，北和中间低，东部沿岸较陡，倾斜角为35度。岛中央主峰伶仃峰高311.8米，天晴时可看到香港、桂山等岛及各岛沿线以内整个海面。外伶仃岛位于珠海市东区，地处珠江入海口，西距市中心27海里，北距香港长洲6海里，面积4.23平方公里，是唯一可看见香港市中心的海岛。</span></span></p><p style=\"text-align:center\"><img src=\" /vipimg/351827.jpg\" title=\"351827.jpg\" alt=\"sea2.jpg\" style=\"width: 696px; height: 377px;\" width=\"696\" height=\"377\"/></p><p><span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: normal; letter-spacing: normal; orphans: 2; text-align: start; text-indent: 28px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\"></span>大家在圆明新园前合影</p><p style=\"text-align:center\"><img src=\" /vipimg/534712.jpg\" title=\"534712.jpg\" alt=\"all.jpg\" style=\"width: 677px; height: 311px;\" width=\"677\" height=\"311\"/></p><p>然后坐船将近2个小时才到伶仃岛</p><p style=\"text-align:center\"><img src=\" /vipimg/104868.jpg\" title=\"104868.jpg\" alt=\"waitesmall.jpg\" style=\"width: 669px; height: 402px;\" width=\"669\" height=\"402\"/></p><p>新楼是新楼, 旧恨依旧, 国家强盛, 国人才得以安生, 今天的幸福生活都来源于前人的心血付出</p><p style=\"text-align:center\"><img src=\" /vipimg/492068.jpg\" title=\"492068.jpg\" alt=\"seaall.jpg\" style=\"width: 683px; height: 406px;\" width=\"683\" height=\"406\"/></p><p>大家最后在伶仃岛拍照留恋, 纪念我们的旅程<br/></p><p><br/></p><p><br/></p><p><br/></p><p><br/></p>', '小尚', '1', '0', '/uploads/20170628/8a41009e466e03c5fb135ea4459c1a14.jpg', null, '0', '0', '2017-06-28 20:42:29', '2017-06-28 20:22:40');

