-- ----------------------------
-- Table structure for think_blog
-- ----------------------------
DROP TABLE IF EXISTS `think_blog`;
CREATE TABLE `think_blog` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'blogID',
  `cid` smallint(5) unsigned NOT NULL COMMENT '分类ID',
  `type` tinyint(1) unsigned DEFAULT '0' COMMENT 'blog类型 1 纯文字 2 一张图 和文字 3 多图加文字',
  `title` varchar(255) DEFAULT '' COMMENT '标题',
  `introduction` varchar(255) DEFAULT '' COMMENT '简介',
  `content` longtext COMMENT '内容',
  `author` varchar(20) DEFAULT '' COMMENT '作者',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '状态 0 待审核  1 审核',
  `reading` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '阅读量',
  `thumb` varchar(255) DEFAULT '' COMMENT '缩略图',
  `photo` text COMMENT '图集',
  `is_top` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶  0 不置顶  1 置顶',
  `sort` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `publish_time` int(11) NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=18 DEFAULT CHARSET=utf8 COMMENT='blog表';

-- ----------------------------
-- Records of think_blog
-- ----------------------------
INSERT INTO `think_blog` VALUES ('1', '1', '1', '顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把', '顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把', '今天，“顺丰控股”在深交所敲钟上市，作为国内快递业的老大，这次的顺丰上市自然吸引了国内资本的目光。甚少露面的顺丰速运创始人、顺丰控股董事长王卫现身敲钟仪式，宣告顺丰正式上市。\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n\r\n一身工作装加上黑框眼镜，似乎很难看出他是身家千亿的顺丰大老板。\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n公开报道称，红包金额最低1888元，最高超过1万元。\r\n\r\n不少员工在微博晒出红包截图\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n去年被打的快递员小哥被请来做敲钟嘉宾\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n\r\n2016年4月17日上午，一位顺丰快递员骑着电三轮在北京送快递，不小心与一辆黑色现代发生刮蹭，现代车司机下车后连续掌掴快递员6次，这个场景被围观者用手机拍下并发到互联网上，引起了社会对快递“小哥”尊严的广泛讨论。\r\n\r\n随后王卫在朋友圈声明一定为员工讨回公道。在今天上市敲钟的仪式中，被打的快递小哥再次现身，这一次他是被王卫邀请而来，成为敲钟的嘉宾之一。\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n\r\n据前顺丰员工李越向记者透露，王卫的贴身保镖大多来历不凡，通常两到三年一换，每届保镖在退役之后，往往会晋升为公司内部的中层干部。“能力强的会成为区域经理，次一点的也会让他留下当区域主管。为王总长期忠诚服务的员工，无论高低，相当于拿到一张终身饭卡。”\r\n\r\n得此老板，夫复何求？\r\n王卫身家也水涨船高到1400多亿元，成为快递业老大。\r\n\r\n今日，顺丰速运上市后，当前股价报55.21元，已经涨停，总市值超越“万科”和“美的”，成为深市第一大市值公司。按照目前的股价计算，其市值高达2310亿。\r\n\r\n在2016年度福布斯中国富豪排行榜中，王卫排名第四。据媒体统计：\r\n\r\n如果顺丰控股接下来再收获1个涨停，王卫即可超越马化腾，跻身第三名；\r\n\r\n如果连续收获3个涨停，即可超越马云；\r\n\r\n而5个涨停之后，就可成为中国首富。\r\n\r\n顺丰上市，总裁掷10亿给员工发红包！被打的快递小哥又火一把\r\n\r\n面对如此豪放的老板，我只想说\r\n\r\n不说了，我去送快递了......', 'rose', '1', '999', 'http://p1.pstatp.com/large/16cb0007d3dd47497bd4', null, '0', '0', '1487397482', '1487397482');
INSERT INTO `think_blog` VALUES ('2', '1', '1', '新华社记者“冒死”亲测福岛 日本核辐射有多严重？', '福岛核泄漏事故,马上就要六周年。\r\n\r\n中国外交部发言人日前表示,中方一向高度关注日本福岛核泄漏事故产生的影响,多次要求日本政府做好事故的后续处理工作。希望日本政府能够就如何采取有效措施切实消除核泄漏事故产生的影响作出负责任的说明。这不仅是对日本国民负责,也是对邻国人民和国际社会负责。', '福岛核泄漏事故,马上就要六周年。\r\n\r\n中国外交部发言人日前表示,中方一向高度关注日本福岛核泄漏事故产生的影响,多次要求日本政府做好事故的后续处理工作。希望日本政府能够就如何采取有效措施切实消除核泄漏事故产生的影响作出负责任的说明。这不仅是对日本国民负责,也是对邻国人民和国际社会负责。\r\n\r\n但日本方面声称,福岛辐射值处于正常水平。\r\n\r\n日本是中国游客出境游的一大选择,2016年估计约有640万人次到访日本。福岛核泄漏疑云再起,在网友中引发争论:带着这一疑问,我们的记者携带测量仪,深入福岛核泄漏事故核心区域,给出答案。\r\n\r\n本文摘编自微信公众号“新华国际”(ID:interxinhua),转载已获授权记者在福岛核事故核心区域拍摄,标语为“有高辐射地区,不建议迁回”\r\n\r\n1\r\n\r\n福岛隔离区:仪器震得手麻\r\n\r\n进入福岛第一核电站后,日本东京电力公司人员要求关闭手机,并换上一套装备,包括手套、头套、两双棉袜、马甲、辐射仪、耳麦等。穿戴后,再换上工作帽和雨靴样式的防护靴,之后坐另外一辆大巴去离机组很近的地方采访。\r\n\r\n在机组附近,记者将辐射仪的警报声关闭,但还止不住狂震:\r\n\r\n“站在离机组几十米远,海拔约30米的高地上往下看,只有2号机组保存完好,其他机组都发生过氢气爆炸。1至3号机组全部发生了堆芯熔化,压力容器内的核燃料棒熔化成了核残渣,如何取出这些核残渣东电目前还毫无办法。”\r\n\r\n“堆芯熔化”是核电站可能发生的事故中最为严重的事态。正如视频里展现,事故周边目前仍是“鬼城状态”。福岛隔离区荒无人烟\r\n\r\n这一切在提醒所有人:按照国际核事件分级表,福岛核事故等级为最高的7级,同1986年苏联切尔诺贝利核电站爆炸和泄漏事故等级相同。\r\n\r\n1986年发生的切尔诺贝利核事故,已过去30余年,60余万人受灾,如今方圆30公里仍是无人区。专家估计,地区恢复到适宜人类活动时间超过1万年。\r\n\r\n2\r\n\r\n不值得信任的日本政府\r\n\r\n“堆芯熔化”这一事态,在事故发生后,东京电力公司一直辩称为“堆芯损伤”,直到两个月后才首次承认“堆芯熔化”。\r\n\r\n按当时东电公司的内部标准,即“堆芯损伤比例超过5%”即为“堆芯熔化”,那么在2011年3月14日就应宣布“堆芯熔化”。2016年6月21日,东电公司社长广濑直己(图左)就公司隐瞒“堆芯熔化”一事道歉。', 'rose', '1', '666', 'http://a2.peoplecdn.cn/ffb51f13c9e6508bb5691cb19ab2a9ad.jpg@1l', null, '0', '0', '1487397482', '1487397482');
INSERT INTO `think_blog` VALUES ('3', '1', '1', '中国4500万人将丢饭碗？或许与你有关', '新世纪以来,中国一直被誉为“世界工厂”,庞大的劳动力、廉价的土地资源和相对成熟的基础设施,从世界各地吸引了大量的资本。然而现今,关于外资是否正从中国撤离的话题,正引发激烈的争论。中国外贸的决战是否已经开始?', '新世纪以来,中国一直被誉为“世界工厂”,庞大的劳动力、廉价的土地资源和相对成熟的基础设施,从世界各地吸引了大量的资本。然而现今,关于外资是否正从中国撤离的话题,正引发激烈的争论。中国外贸的决战是否已经开始?\r\n\r\n\r\n\r\n外资出走敲响警钟,波及中国4500万就业岗位\r\n\r\n就在近两年,关于外资从中国撤离的消息屡屡被曝光。\r\n\r\n\r\n\r\n值得关注的是外商在固定资产投资方面的暴跌。据国家统计局数据,2016年外资在中国固定资产的投资额仅为1211.97亿,对比11年3269.81亿这一数字,短短五年时间便下跌了62.94%。\r\n\r\n另外,2月16日,据商务部公布的数据显示,今年1月份,中国实际使用外资额为120亿美元,同比下跌14.73%。这更让“外资撤潮说”重新成为热门话题。权威的解读认为月度数据不代表长期趋势,但从实际情况来看,每一次外资撤离都涉及到人数众多的裁员。\r\n\r\n根据官方估算,全部外商投资企业吸纳的直接就业人数超过4500万。而外资的不断撤出,可能将影响这一庞大就业人群的生计!\r\n\r\n谁抢了中国人的工作?\r\n\r\n\r\n\r\n究竟为什么外资企业撤离中国?究竟是谁抢了中国人的工作?\r\n\r\n1第一,中国的生产成本高了。\r\n\r\n近年广东等地的土地成本持续上扬,税收优惠的力度减小,外资在华的投资收益率越来越低。\r\n\r\n在用工成本涨势惊人的情况下,中国劳动力的工资增速却未曾降低,多个年份甚至是全球第一。这代表着外资在华能捞到的油水越发稀薄了。\r\n\r\n2第二,对外资政策准入门槛提高,优惠政策取消。\r\n\r\n中国许多地区都提高了投资门槛,对新进的外资企业,不仅没有专门的优惠政策,甚至还对其产业类型、节能减排方面提出很高的要求。此外税收上,外资企业的“超国民待遇”也正在失效。\r\n\r\n3第三,中国劳动力不再廉价。\r\n\r\n由于货币量化宽松,导致物价上涨,又反作用于食品价格和劳动力价格,中国劳动力没那么物美价廉了。\r\n\r\n4第四,市场竞争愈发激烈。\r\n\r\n除了以上原因外,也有部分外企自身经营不善,转型不及时才被迫退出中国,例如诺基亚、摩托罗拉等知名企业。因中国市场竞争激烈,利润下降而退出中国市场的外企,也大有存在。\r\n\r\n\r\n\r\n我们还不得不正视一个问题:外资企业尚可撤出中国,国内企业会如何反应?他们大多对海外市场的运营一无所知。较高的企业经营成本,会迫使资本从实体经济(尤其是劳动密集型行业)中退出,不仅重创就业市场,进一步恶化经济衰退的趋势,还会加剧资产的泡沫化,增加金融风险。对此,决策者应当慎重应对。\r\n\r\n谁都不想失业,但未来的风险难以预料。或许我们每个人都得好好想想,如果突然一天失业了,该怎么办?\r\n\r\n如果不幸失业了你该何去何从?\r\n\r\n\r\n\r\n1关键是掌握主动,先发才能制人\r\n\r\n有人曾分享了这样的故事:他的好友在入职没多久就遇到了行业系统性风险。在当时别的同事还在体制内随遇而安时,他就已开始寻找新目标,在半年时间内去了一家更优秀的企业。等各种坑爹制度出台,大家都怨声载道的时候,只有他在继续自己的梦想。\r\n\r\n其实,无论我们是在大公司还是小公司,是在体制内还是体制外,都应该把“随时可以离开公司”当成目标,摆脱舒适区,提前储备多项足以立身的能力。\r\n\r\n2注意培养你的“睡后收入”\r\n\r\n为什么我们辛辛苦苦工作,却赶不上房价增长的速度?\r\n\r\n为什么我们即使很勤奋、很努力,但还是感到越来越穷?\r\n\r\n为什么在那些说着1个亿小目标的人面前,我们好像完全看不到希望?\r\n\r\n因为当今世界,劳动无法跑赢资本!光靠自己打工和工资收入是远远不够的。资本收入、知识产权收入这类“睡后收入”才能让我们更有安全感。\r\n\r\n\r\n\r\n3如何增加资本收入呢?\r\n\r\n普通人可以这两大块着手:储蓄&投资。\r\n\r\n第一阶段:储蓄。只要养成好的理财习惯,在早期工作的时候注重积累,那么你将积累一大笔本金。常见的储蓄工具有银行存款、国债、银行理财、货币基金等。\r\n\r\n第二阶段:投资。投资,简单的说就是购买理财产品,达到钱生钱的目的。高风险的投资品可以让你迅速致富,但亏损的可能性也非常大,最好通过专业人士打理,例如购买基金。中低风险的固收类投资品就跟房租似的,能给你带来稳定的持续回报,例如债券、信托等。这里需要注意的是,不论哪类投资都有风险,大家需要谨慎对待。\r\n\r\n\r\n\r\n来源:中金在线(ID:cnfol-com)综合面包财经(ID:mbcaijing)、论制造业(ID:sibu-999888)、凤凰财经(ID:finance_ifeng)、工人日报客户端、好规划网', 'rose', '1', '666', 'http://a3.peoplecdn.cn/abad5975e3971106e161526788a1416c.jpg@1l', null, '0', '0', '1487397483', '1487397483');
INSERT INTO `think_blog` VALUES ('4', '1', '0', '1111', '', '1111', '', '1', '0', '', null, '0', '0', '0', '1488764122');
INSERT INTO `think_blog` VALUES ('5', '1', '0', '十二届全国人大五次会议在京开幕', '', '&lt;h1 style=&quot;margin:0px 0px 15px;padding:0px;font-size:26px;font-weight:normal;font-family:&amp;quot;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;新华社北京3月5日电 第十二届全国人民代表大会第五次会议5日上午在人民大会堂开幕。国务院总理李克强向大会作政府工作报告时指出，使命重在担当，实干铸就辉煌。我们要更加紧密地团结在以习近平同志为核心的党中央周围，同心同德，开拓进取，努力完成今年经济社会发展目标任务，为实现“两个一百年”宏伟目标、建设富强民主文明和谐的社会主义现代化国家、实现中华民族伟大复兴的中国梦而不懈奋斗。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img src=&quot;http://inews.gtimg.com/newsapp_bt/0/1217115551/641&quot; style=&quot;vertical-align:middle;max-width:640px;display:block;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;3月5日，第十二届全国人民代表大会第五次会议在北京人民大会堂开幕。党和国家领导人习近平、李克强、俞正声、刘云山、王岐山、张高丽等出席会议。新华社记者 兰红光 摄&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img src=&quot;http://inews.gtimg.com/newsapp_bt/0/1217010147/641&quot; style=&quot;vertical-align:middle;max-width:640px;display:block;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;3月5日，第十二届全国人民代表大会第五次会议在北京人民大会堂开幕。 新华社记者 庞兴雷 摄&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;今天的人民大会堂气氛庄严热烈。万人大礼堂内灯火辉煌，主席台帷幕中央国徽高悬，十面红旗分列两侧。近3000名全国人大代表肩负人民重托出席盛会，认真履行宪法和法律赋予的神圣职权。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;大会主席团常务主席、执行主席张德江主持大会。大会主席团常务主席、执行主席李建国、王胜俊、陈昌智、严隽琪、王晨、沈跃跃、吉炳轩、张平、向巴平措、艾力更·依明巴海、万鄂湘、张宝文、陈竺在主席台执行主席席就座。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;习近平、李克强、俞正声、刘云山、王岐山、张高丽和大会主席团成员在主席台就座。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;十二届全国人大五次会议应出席代表2924人。今天的会议，出席2862人，缺席62人，出席人数符合法定人数。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;上午9时，张德江宣布：中华人民共和国第十二届全国人民代表大会第五次会议开幕。会场全体起立，高唱国歌。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;根据会议议程，国务院总理李克强代表国务院向大会作政府工作报告。报告共分三部分：一、2016年工作回顾；二、2017年工作总体部署；三、2017年重点工作任务。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img src=&quot;http://inews.gtimg.com/newsapp_bt/0/1217148886/641&quot; style=&quot;vertical-align:middle;max-width:640px;display:block;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;3月5日，第十二届全国人民代表大会第五次会议在北京人民大会堂开幕。国务院总理李克强作政府工作报告。 新华社记者 庞兴雷 摄&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强说，过去一年，我国发展面临国内外诸多矛盾叠加、风险隐患交汇的严峻挑战。在以习近平同志为核心的党中央坚强领导下，全国各族人民迎难而上，砥砺前行，推动经济社会持续健康发展。党的十八届六中全会正式明确习近平总书记的核心地位，体现了党和人民的根本利益，对保证党和国家兴旺发达、长治久安，具有十分重大而深远的意义。各地区、各部门不断增强政治意识、大局意识、核心意识、看齐意识，推动全面建成小康社会取得新的重要进展，全面深化改革迈出重大步伐，全面依法治国深入实施，全面从严治党纵深推进，全年经济社会发展主要目标任务圆满完成，“十三五”实现了良好开局。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强从继续创新和加强宏观调控，经济运行保持在合理区间；着力抓好“三去一降一补”，供给结构有所改善；大力深化改革开放，发展活力进一步增强；强化创新引领，新动能快速成长；促进区域城乡协调发展，新的增长极增长带加快形成；加强生态文明建设，绿色发展取得新进展；注重保障和改善民生，人民群众获得感增强；推进政府建设和治理创新，社会保持和谐稳定等方面回顾了过去一年的工作。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强指出，我们也清醒看到，经济社会发展中还存在不少困难和问题。我们一定要直面挑战，敢于担当，全力以赴做好政府工作，不辱历史使命，不负人民重托。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强说，2017年发展的主要预期目标是：国内生产总值增长6.5%左右，在实际工作中争取更好结果；居民消费价格涨幅3%左右；城镇新增就业1100万人以上，城镇登记失业率4.5%以内；进出口回稳向好，国际收支基本平衡；居民收入和经济增长基本同步；单位国内生产总值能耗下降3.4%以上，主要污染物排放量继续下降。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强报告了做好2017年政府工作要把握好的五点：一是贯彻稳中求进工作总基调，保持战略定力；二是坚持以推进供给侧结构性改革为主线；三是适度扩大总需求并提高有效性；四是依靠创新推动新旧动能转换和结构优化升级；五是着力解决人民群众普遍关心的突出问题。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强说，2017年要重点做好九个方面工作：一是用改革的办法深入推进“三去一降一补”；二是深化重要领域和关键环节改革；三是进一步释放国内需求潜力；四是以创新引领实体经济转型升级；五是促进农业稳定发展和农民持续增收；六是积极主动扩大对外开放；七是加大生态环境保护治理力度；八是推进以保障和改善民生为重点的社会建设；九是全面加强政府自身建设。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强还就民族、宗教和侨务工作，国防和军队建设，香港、澳门发展和两岸关系，以及我国外交政策作了阐述。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;根据会议议程，大会审查2016年国民经济和社会发展计划执行情况与2017年国民经济和社会发展计划草案的报告及2017年计划草案、2016年中央和地方预算执行情况与2017年中央和地方预算草案的报告及2017年全国预算草案。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在主席台就座的还有：马凯、王沪宁、刘延东、刘奇葆、许其亮、孙春兰、孙政才、李源潮、汪洋、张春贤、范长龙、孟建柱、赵乐际、胡春华、栗战书、郭金龙、韩正、杜青林、赵洪祝、杨晶、常万全、杨洁篪、郭声琨、王勇、周强、曹建明、韩启德、帕巴拉·格列朗杰、董建华、万钢、林文漪、罗富和、何厚铧、张庆黎、李海峰、陈元、卢展工、周小川、王家瑞、王正伟、马飚、齐续春、陈晓光、马培华、刘晓峰、王钦敏，以及中央军委委员房峰辉、张阳、赵克石、张又侠、吴胜利、马晓天、魏凤和等。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;香港特别行政区行政长官&lt;/span&gt;&lt;a class=&quot;a-tips-Article-QQ&quot; target=&quot;_blank&quot; href=&quot;http://t.qq.com/LeungChunying?pref=qqcom.keyword&quot; style=&quot;text-decoration:none;outline:0px;color:#000000;border-bottom:1px dotted #536DA6;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;梁振英&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;、澳门特别行政区行政长官崔世安列席会议并在主席台就座。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;出席全国政协十二届五次会议的政协委员列席大会。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;中央和国家机关有关部门、解放军及武警部队、各人民团体有关负责人列席或旁听了大会。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;各国驻华使节旁听了大会。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;span style=&quot;font-size:9px;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/h1&gt;', '', '1', '0', '', null, '0', '0', '0', '1488764237');
INSERT INTO `think_blog` VALUES ('6', '1', '0', '中国的心理咨询开始火了，然后呢？', '', '&lt;div class=&quot;daoyu&quot; style=&quot;margin:10px 0px;padding:0px;text-indent:5em;position:relative;font-size:16px;line-height:34px;font-family:&amp;quot;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p id=&quot;shareintro&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在中国，心理咨询并不是一个新词，但心理咨询行业却长期不温不火。最近不少媒体突然发现，心理咨询行业火了起来。这个行业因何火爆，势头能否持续，普通人何时能享受到物美价廉的心理咨询服务，成为很多人关心的话题。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;focus&quot; style=&quot;margin:60px 0px 80px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;div class=&quot;tit&quot; style=&quot;margin:15px 0px;padding:0px 0px 0px 15px;&quot;&gt;\r\n		&lt;h3 style=&quot;margin:0px;padding:0px;width:705px;height:20px;text-indent:-999em;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/ydxd_topic.png&amp;quot;&quot;&gt;\r\n			&lt;span style=&quot;font-size:16px;&quot;&gt;要点速读&lt;/span&gt;\r\n		&lt;/h3&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;focus_list&quot; style=&quot;margin:0px;padding:0px;position:relative;overflow:hidden;&quot;&gt;\r\n		&lt;span class=&quot;t focus_top&quot; style=&quot;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/topicallpic.png&amp;quot;display:block;width:6px;height:5px;position:absolute;top:0px;left:0px;z-index:3;&quot;&gt;&lt;/span&gt;&lt;span class=&quot;t focus_bottom&quot; style=&quot;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/topicallpic.png&amp;quot;display:block;width:6px;height:8px;position:absolute;bottom:0px;left:0px;z-index:3;&quot;&gt;&lt;/span&gt;&lt;em style=&quot;width:720px;height:1px;display:block;position:absolute;left:0px;bottom:0px;background-color:#EDEDF5;z-index:2;&quot;&gt;&lt;/em&gt;\r\n		&lt;ul style=&quot;list-style-position:initial;list-style-image:initial;position:relative;overflow:hidden;text-align:center;background-color:#EDEDF5;border-left:6px solid #9A9DB7;&quot;&gt;\r\n			&lt;li data-text=&quot;面对迅速发展的心理咨询业，公众关心的是，何时能享受到物美价廉的心理服务。&quot; class=&quot;inline-block&quot; style=&quot;padding-top:25px;padding-bottom:25px;width:645px;position:relative;display:inline-block;border-bottom:1px solid #969696;text-align:left;&quot;&gt;\r\n				&lt;span style=&quot;text-align:center;display:block;width:28px;height:37px;line-height:30px;position:absolute;left:0px;top:28px;font-size:16px;color:#E5E5ED;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/yaodbg.png&amp;quot;&quot;&gt;1&lt;/span&gt;&lt;a style=&quot;font-size:18px;display:inline-block;line-height:28px;padding-left:50px;cursor:default;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;面对迅速发展的心理咨询业，公众关心的是，何时能享受到物美价廉的心理服务。&lt;/span&gt;&lt;/a&gt;\r\n			&lt;/li&gt;\r\n			&lt;li data-text=&quot;无论从公众福祉角度，还是从投资回报角度考虑，都应该加大对精神卫生领域的投入。&quot; class=&quot;inline-block&quot; style=&quot;padding-top:25px;padding-bottom:25px;width:645px;position:relative;display:inline-block;border-bottom:1px solid #969696;text-align:left;&quot;&gt;\r\n				&lt;span style=&quot;text-align:center;display:block;width:28px;height:37px;line-height:30px;position:absolute;left:0px;top:28px;font-size:16px;color:#E5E5ED;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/yaodbg.png&amp;quot;&quot;&gt;2&lt;/span&gt;&lt;a style=&quot;font-size:18px;display:inline-block;line-height:28px;padding-left:50px;cursor:default;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;无论从公众福祉角度，还是从投资回报角度考虑，都应该加大对精神卫生领域的投入。&lt;/span&gt;&lt;/a&gt;\r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;有了钱心更烦，中国的心理咨询火了起来&lt;/span&gt;\r\n	&lt;/h2&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;最近两年，中国心理咨询行业迎来了一个小高潮。一方面是消费者人数不断增加，另一方面是咨询机构数量增长迅猛——心理咨询平台简单心理联合北京大学心理咨询与治疗中心发布的《2016心理健康认知度与心理咨询行业调查报告》（简称“报告”）显示，传统实体心理咨询机构从（2009年-2012年）200家，增加到（2013年至今）357家；基于互联网的心理咨询产品从2014年前的1家增加到现在的64家。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148867553533172.jpg&quot; alt=&quot;心理咨询机构，尤其是互联网心理咨询机构数量近两年出现井喷式增长。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; title=&quot;心理咨询机构，尤其是互联网心理咨询机构数量近两年出现井喷式增长。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;心理咨询机构，尤其是互联网心理咨询机构数量近两年出现井喷式增长。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;中国心理咨询产业近两年的发展，其来有自。国外的经验表明，在丰衣足食之后，经济继续增长未必能提升人们的幸福感，相反，人们的幸福感会出现停滞或下降，心理疾病的发病率可能提高。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;中国也不例外。一方面，90年代以来经济社会的剧烈变动，让人们的心理受到了前所未有的冲击。世界卫生组织的一份调查显示，中国大概有1.9亿人在一生中需要接受专业的心理咨询或心理治疗。另一方面，随着经济的增长，中国户均可支配收入在5000元-8000元/月的“新兴中间层”和1.2万元-2.2万元/月的“中上层”人群比例不断提升。这些人有能力和意愿为心理咨询付费。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;更重要的是，互联网技术的发展，让心理学科普变得更容易，大量网络心理咨询平台的出现，拉近了患者和心理咨询师的距离。于是，我们看到了心理咨询行业爆发式的增长。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;心理咨询行业继续发展面临的现实困境不小&lt;/span&gt;\r\n	&lt;/h2&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;心理咨询行业迎来了春天，但这个春天能否持久，还要看行业发展面临的问题能否得到解决。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;首先，公众对进行心理咨询仍有认知误区。受传统观念的影响，不少人仍把心理问题看成性格软弱或意志力不足的表现，认为只有得了“精神病”，才要看心理医生。这种社会认知，对敢于进行心理咨询的人也有压力，参与调查报告的5000名付费来访者中，有14.4%的来访者会刻意隐瞒自己曾经或者进行咨询的事实。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;其次，是心理咨询的费用问题。由于中国心理咨询尚未被纳入医保，心理咨询费用需要消费者承担。目前，国内一线城市心理咨询师的收费已经达到300-1500/小时，即便如此，很多心理咨询机构，“收入一半给咨询师，剩下的一半贴进硬成本，盈利空间依然有限”。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148866755497219.jpg&quot; alt=&quot;为了招揽顾客，很多实体心理咨询机构设在市中心，租金成本很高&quot; title=&quot;为了招揽顾客，很多实体心理咨询机构设在市中心，租金成本很高&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;为了招揽顾客，很多实体心理咨询机构设在市中心，租金成本很高&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;但对普通人来说，这个价格明显太高。2015年，一项针对广州某社区进行的调查显示，超过85%的居民认为心理咨询价格应该在每小时100元以内，认为合理收费为200元以上的仅有2.1%。如果心理咨询的费用依然不纳入医保，也无法降低成本，心理咨询可能会长期处于“叫好不叫座”的局面。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;再次，心理咨询从业者依然良莠不齐。北京大学心理学系教授钱铭怡指出，国际上，心理咨询师首先必须是经过本专业的硕士、博士培训，再进行800—2000小时的实习，才能上岗。 而在中国，按照《心理咨询师国家职业标准》，一个没有任何相关知识背景的本科生，只需交几千块钱培训一段时间，就能考取国家三级心理咨询师执业证书。心理咨询师的上岗培训时间则常不到国际水平的十分之一。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;对看不见摸不着的心理服务，公众本来就对其效果存疑；从业人员如此选拔，只会加重公众的疑虑。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;与“城里人”相比，农村人口对心理咨询的需求更为迫切&lt;/span&gt;\r\n	&lt;/h2&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;不少人觉得，心理问题是物质需求得到满足后的“富贵病”，并把它当作“城里人”的专利。实际上，农村人口的心理问题可能更为严重。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;据环球网报道，《柳叶刀》杂志一项针对中印两国精神疾病诊疗状况的研究发现（2016年），中印目前均缺乏专业的精神健康专家，患者就医困难，这种状况在乡村地区尤为突出。在一些地区，出现明显精神失调症状的患者无法得到诊断，更不要说获得相应的心理治疗。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;中国科学院心理研究所《我国心理咨询与心理治疗发展现状、问题与对策》（2016年）则发现，目前中国的发达与落后地区，公众心理健康需求已呈现不同特征。在发达地区，随着居民收入增加，个人主动的心理咨询与治疗服务需求大幅上升。而在贫困地区，居民无力承担心理健康服务费用，甚至因各种原因无法意识到自身心理健康需求，但其心理健康问题往往更加严重，由此衍生的生理疾病、刑事案件等亦更为沉重。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148866913067807.jpg&quot; alt=&quot;愿意付费进行心理咨询的消费者集中于大城市。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; title=&quot;愿意付费进行心理咨询的消费者集中于大城市。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;愿意付费进行心理咨询的消费者集中于大城市。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;更严峻的是，目前我国有900多万留守儿童，3000多万流动儿童。有研究显示，早期处于不利环境的儿童，成人后罹患多种心理疾病患的几率高于普通人群。他们的心理健康状况远比城里人需要关注。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148866814920470.jpg&quot; alt=&quot;农村儿童的心理健康问题更需关注&quot; title=&quot;农村儿童的心理健康问题更需关注&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;农村儿童的心理健康问题更需关注&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;增加财政投入，让普通人负担得起心理服务，会有丰厚的回报&lt;/span&gt;\r\n	&lt;/h2&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;去年10月，中共中央、国务院印发了《“健康中国2030”规划纲要》。纲要强调，“加强对抑郁症、焦虑症等常见精神障碍和心理行为问题的干预，加大对重点人群心理问题早期发现和及时干预力度”，这无疑是心理咨询行业发展的巨大推力。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;然而，不少人却认为，在人均收入达到发达国家水平之前，心理咨询注定是奢侈品，普通人无法得到。实际上，这种看法有些想当然。《柳叶刀》的相关研究指出，中印两国精神病患得不到所需诊疗的一个重要原因是投入不足——中印两国在精神卫生方面的投入占医疗总预算的比重均不到1%，而美国的投入约占医疗总预算的6%，德国和法国则达10%以上。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;即使不跟发达国家比，中国在精神卫生方面的投入也该提高。世卫组织2014年精神卫生地图集显示，各国政府在精神卫生方面的支出平均占医疗总预算的3%，低收入国家才不足1%，中国2014人均GDP为6995美元，按照世界银行的标准，中国已属于中等偏上收入国家。如果政府能加大投入，将心理咨询纳入医保，负担减轻的消费者会更愿意进行心理咨询，有利于行业继续发展；“富起来”的咨询机构也可以通过网络，为农村人口提供高质量的心理服务，一举多得。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;过去，人们认为加大精神卫生投入，可以增进公民的健康和福祉，减少人口老龄化后认知障碍症的爆发。世界卫生组织2016年的一项最新研究，则给了加大精神卫生投入新的有力论据。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;世卫组织发现，目前一些常见精神病患的数量不断上升，1990年和2013年期间，患有抑郁症、焦虑症或躁郁症的人数上升了近50%，亟需各国加大投入。世卫组织的研究计算了2016-2030年间，36个低收入、中等收入和高收入国家可能发生的相关治疗费用和健康结果。研究认为，以心理咨询和抗抑郁药物为主的扩大治疗费用估计会达到1470亿美元，但所获得的回报会远远超出成本——公民工作能力提升所带来的价值为3990亿美元，身体健康状况的改善还会额外增加3100亿美元的收益。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;这意味着在精神卫生上投入1美元的投入可获得4倍多的回报。基于此，世卫组织总干事陈冯富珍表示，“现在我们必须找出办法，确保所有男人、妇女和儿童，无论生活在何处，都能真正获得精神卫生服务”。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;/p&gt;\r\n&lt;div class=&quot;summary&quot; style=&quot;margin:0px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;div class=&quot;summary_tit&quot; style=&quot;margin:30px 0px 0px;padding:0px;width:720px;height:95px;text-align:center;&quot;&gt;\r\n		&lt;h2 style=&quot;margin:0px;padding:0px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/topicallpic.png&amp;quot;display:inline-block;width:95px;height:95px;&quot;&gt;\r\n		&lt;/h2&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;summary_text&quot; style=&quot;margin:20px 0px 0px;padding:0px;line-height:36px;text-indent:2em;&quot;&gt;\r\n		&lt;p style=&quot;margin-top:0px;margin-bottom:0px;padding:0px;&quot;&gt;\r\n			&lt;span style=&quot;font-size:16px;&quot;&gt;加大精神卫生投入，让普通人也能够负担心理咨询的费用，不是乱花钱，而是回报丰厚的投资。何乐而不为呢？&lt;/span&gt;\r\n		&lt;/p&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;', '', '1', '0', '', null, '0', '0', '1488765082', '1488764980');
