-- ----------------------------
-- Table structure for think_document
-- ----------------------------
DROP TABLE IF EXISTS `think_document`;
CREATE TABLE `think_document` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'blogID',
  `cid` smallint(5) unsigned NOT NULL COMMENT '分类ID',
  `id_name` varchar(100) DEFAULT NULL,
  `type` tinyint(1) unsigned DEFAULT '0' COMMENT 'blog类型 1 纯文字 2 一张图 和文字 3 多图加文字',
  `title` varchar(255) DEFAULT '' COMMENT '标题',
  `content` longtext COMMENT '内容',
  `wechatname` varchar(255) DEFAULT NULL,
  `thumb` varchar(255) DEFAULT NULL,
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `modification_time` int(11) DEFAULT NULL,
  `web_type` int(5) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=33 DEFAULT CHARSET=utf8 COMMENT='blog表';

-- ----------------------------
-- Records of think_document
-- ----------------------------
INSERT INTO `think_document` VALUES ('1', '1', 'test2', '1', '被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！', '&lt;p&gt;\r\n	&lt;strong&gt;陈小春演唱会上演绎了“你在闹我在笑”，应采儿在台下搞怪献吻，陈小春温柔一笑，亮瞎观众的眼。据传陈小春当初娶应采儿的时候，为表忠心签了“卖身契”。然而那些条款真的是霸王条例，没有几个男人可以那么疼老婆的，陈小春真的是把老婆宠上天了。&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/191b0000d9742d352699&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/1915000102d6fc9994e8&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;范玮琪&lt;br /&gt;\r\n&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;自从范玮琪和黑人陈建州结婚了，陈建州就成了炫妻狂魔，他的微博全都是范范......黑子自己说每天晚上都要牵着范范的手睡觉，希望他们一直幸福，一生一世一双人。&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/19160000dcebae5af397&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p9.pstatp.com/large/191a0001075cd5d04a83&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;Angelababy&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;黄晓明用一个世纪婚礼迎娶baby，说明了baby在他心里的重要，baby怀孕后，教主更是时时刻刻照顾，两人非常甜蜜，娱乐圈黄晓明已经是宠妻狂魔了，看baby眼神里都是慢慢的爱。&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/191a000106b907a7af66&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p1.pstatp.com/large/191700010835349a263f&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;刘诗诗&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;两人相差16岁，但是年龄不影响感情，吴奇隆对刘诗诗多好大家有目共睹，为了给诗诗一个完美婚礼，一向节俭的吴奇隆对记者说：对于婚礼没有预算。更是拿出10亿聘礼迎娶刘诗诗。在机场记者拍到的画面，永远是四爷在推行李，诗诗小鸟依人在旁边。&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/191b0000e34646c38c15&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/19160000e467af20fbab&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;徐濠萦&lt;br /&gt;\r\n&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;人人都说徐濠萦败家，但是陈奕迅说：我的钱就是挣给她花的。为博老婆一笑，买豪宅，买名牌。陈奕迅歌唱得好，戏也演的棒，出道多年零绯闻，这就是真爱了，一生一世只爱一人。&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/19180001115e9620805b&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p2.pstatp.com/large/19160000e7744f437126&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;贾静雯&lt;br /&gt;\r\n&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;strong&gt;自从贾静雯和修杰楷两人成婚，硬是把生活过成了偶像剧，两人年龄差和女方的经历，都让吃瓜群众认为不合适。但是鞋子适不适合只有脚知道，两口子现在甜甜蜜蜜，真的是羡煞旁人。&lt;/strong&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p3.pstatp.com/large/1915000111c0fec081ab&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;img src=&quot;http://p2.pstatp.com/large/1917000113114f2e5503&quot; alt=&quot;被老公宠溺，活的像公主的6位女星，10亿聘礼，一生只爱一人！&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;strong&gt;一生只爱一人，才是最美好的婚姻！&lt;/strong&gt;', '[\"qwe123\",\"asd123\",\"321zxc\"]', '/uploads/20170307/e81d4b42dbf44530235c198747274e71.jpg', '1487397482', '1489473014', '1');
INSERT INTO `think_document` VALUES ('2', '1', 'test1', '1', '新华社记者“冒死”亲测福岛 日本核辐射有多严重？', '福岛核泄漏事故,马上就要六周年。\r\n中国外交部发言人日前表示,中方一向高度关注日本福岛核泄漏事故产生的影响,多次要求日本政府做好事故的后续处理工作。希望日本政府能够就如何采取有效措施切实消除核泄漏事故产生的影响作出负责任的说明。这不仅是对日本国民负责,也是对邻国人民和国际社会负责。\r\n但日本方面声称,福岛辐射值处于正常水平。\r\n日本是中国游客出境游的一大选择,2016年估计约有640万人次到访日本。福岛核泄漏疑云再起,在网友中引发争论:带着这一疑问,我们的记者携带测量仪,深入福岛核泄漏事故核心区域,给出答案。\r\n本文摘编自微信公众号“新华国际”(ID:interxinhua),转载已获授权记者在福岛核事故核心区域拍摄,标语为“有高辐射地区,不建议迁回”\r\n1\r\n福岛隔离区:仪器震得手麻\r\n进入福岛第一核电站后,日本东京电力公司人员要求关闭手机,并换上一套装备,包括手套、头套、两双棉袜、马甲、辐射仪、耳麦等。穿戴后,再换上工作帽和雨靴样式的防护靴,之后坐另外一辆大巴去离机组很近的地方采访。\r\n在机组附近,记者将辐射仪的警报声关闭,但还止不住狂震:\r\n“站在离机组几十米远,海拔约30米的高地上往下看,只有2号机组保存完好,其他机组都发生过氢气爆炸。1至3号机组全部发生了堆芯熔化,压力容器内的核燃料棒熔化成了核残渣,如何取出这些核残渣东电目前还毫无办法。”\r\n“堆芯熔化”是核电站可能发生的事故中最为严重的事态。正如视频里展现,事故周边目前仍是“鬼城状态”。福岛隔离区荒无人烟\r\n这一切在提醒所有人:按照国际核事件分级表,福岛核事故等级为最高的7级,同1986年苏联切尔诺贝利核电站爆炸和泄漏事故等级相同。\r\n1986年发生的切尔诺贝利核事故,已过去30余年,60余万人受灾,如今方圆30公里仍是无人区。专家估计,地区恢复到适宜人类活动时间超过1万年。\r\n2\r\n不值得信任的日本政府\r\n“堆芯熔化”这一事态,在事故发生后,东京电力公司一直辩称为“堆芯损伤”,直到两个月后才首次承认“堆芯熔化”。\r\n按当时东电公司的内部标准,即“堆芯损伤比例超过5%”即为“堆芯熔化”,那么在2011年3月14日就应宣布“堆芯熔化”。2016年6月21日,东电公司社长广濑直己(图左)就公司隐瞒“堆芯熔化”一事道歉。', '[\"bxt112\",\"btx222\",\"wer022\"]', '/uploads/20170307/e3e4e180f9530f435949b168f1c6fc9d.jpg', '1487397482', '1488854319', '1');
INSERT INTO `think_document` VALUES ('3', '1', 'test2', '1', '中国4500万人将丢饭碗？或许与你有关', '新世纪以来,中国一直被誉为“世界工厂”,庞大的劳动力、廉价的土地资源和相对成熟的基础设施,从世界各地吸引了大量的资本。然而现今,关于外资是否正从中国撤离的话题,正引发激烈的争论。中国外贸的决战是否已经开始?\r\n外资出走敲响警钟,波及中国4500万就业岗位\r\n就在近两年,关于外资从中国撤离的消息屡屡被曝光。\r\n值得关注的是外商在固定资产投资方面的暴跌。据国家统计局数据,2016年外资在中国固定资产的投资额仅为1211.97亿,对比11年3269.81亿这一数字,短短五年时间便下跌了62.94%。\r\n另外,2月16日,据商务部公布的数据显示,今年1月份,中国实际使用外资额为120亿美元,同比下跌14.73%。这更让“外资撤潮说”重新成为热门话题。权威的解读认为月度数据不代表长期趋势,但从实际情况来看,每一次外资撤离都涉及到人数众多的裁员。\r\n根据官方估算,全部外商投资企业吸纳的直接就业人数超过4500万。而外资的不断撤出,可能将影响这一庞大就业人群的生计!\r\n谁抢了中国人的工作?\r\n究竟为什么外资企业撤离中国?究竟是谁抢了中国人的工作?\r\n1第一,中国的生产成本高了。\r\n近年广东等地的土地成本持续上扬,税收优惠的力度减小,外资在华的投资收益率越来越低。\r\n在用工成本涨势惊人的情况下,中国劳动力的工资增速却未曾降低,多个年份甚至是全球第一。这代表着外资在华能捞到的油水越发稀薄了。\r\n2第二,对外资政策准入门槛提高,优惠政策取消。\r\n中国许多地区都提高了投资门槛,对新进的外资企业,不仅没有专门的优惠政策,甚至还对其产业类型、节能减排方面提出很高的要求。此外税收上,外资企业的“超国民待遇”也正在失效。\r\n3第三,中国劳动力不再廉价。\r\n由于货币量化宽松,导致物价上涨,又反作用于食品价格和劳动力价格,中国劳动力没那么物美价廉了。\r\n4第四,市场竞争愈发激烈。\r\n除了以上原因外,也有部分外企自身经营不善,转型不及时才被迫退出中国,例如诺基亚、摩托罗拉等知名企业。因中国市场竞争激烈,利润下降而退出中国市场的外企,也大有存在。\r\n我们还不得不正视一个问题:外资企业尚可撤出中国,国内企业会如何反应?他们大多对海外市场的运营一无所知。较高的企业经营成本,会迫使资本从实体经济(尤其是劳动密集型行业)中退出,不仅重创就业市场,进一步恶化经济衰退的趋势,还会加剧资产的泡沫化,增加金融风险。对此,决策者应当慎重应对。\r\n谁都不想失业,但未来的风险难以预料。或许我们每个人都得好好想想,如果突然一天失业了,该怎么办?\r\n如果不幸失业了你该何去何从?\r\n1关键是掌握主动,先发才能制人\r\n有人曾分享了这样的故事:他的好友在入职没多久就遇到了行业系统性风险。在当时别的同事还在体制内随遇而安时,他就已开始寻找新目标,在半年时间内去了一家更优秀的企业。等各种坑爹制度出台,大家都怨声载道的时候,只有他在继续自己的梦想。\r\n其实,无论我们是在大公司还是小公司,是在体制内还是体制外,都应该把“随时可以离开公司”当成目标,摆脱舒适区,提前储备多项足以立身的能力。\r\n2注意培养你的“睡后收入”\r\n为什么我们辛辛苦苦工作,却赶不上房价增长的速度?\r\n为什么我们即使很勤奋、很努力,但还是感到越来越穷?\r\n为什么在那些说着1个亿小目标的人面前,我们好像完全看不到希望?\r\n因为当今世界,劳动无法跑赢资本!光靠自己打工和工资收入是远远不够的。资本收入、知识产权收入这类“睡后收入”才能让我们更有安全感。\r\n3如何增加资本收入呢?\r\n普通人可以这两大块着手:储蓄&amp;amp;投资。\r\n第一阶段:储蓄。只要养成好的理财习惯,在早期工作的时候注重积累,那么你将积累一大笔本金。常见的储蓄工具有银行存款、国债、银行理财、货币基金等。\r\n第二阶段:投资。投资,简单的说就是购买理财产品,达到钱生钱的目的。高风险的投资品可以让你迅速致富,但亏损的可能性也非常大,最好通过专业人士打理,例如购买基金。中低风险的固收类投资品就跟房租似的,能给你带来稳定的持续回报,例如债券、信托等。这里需要注意的是,不论哪类投资都有风险,大家需要谨慎对待。\r\n来源:中金在线(ID:cnfol-com)综合面包财经(ID:mbcaijing)、论制造业(ID:sibu-999888)、凤凰财经(ID:finance_ifeng)、工人日报客户端、好规划网', '[\"baixingtang01\",\"baixingtang02\",\"baixingtang03\"]', '/uploads/20170307/8f9fcb76b603caabbcc77bbc692afc54.jpg', '1487397483', '1488854216', '1');
INSERT INTO `think_document` VALUES ('4', '1', 'test1', '0', '蔡小松：当代水墨是中国崛起最诗意的注解', '&lt;h1 style=&quot;margin:0px 0px 15px;padding:0px;font-size:26px;font-weight:normal;font-family:&amp;quot;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img alt=&quot;蔡小松：当代水墨是中国崛起最诗意的注解&quot; src=&quot;http://img1.gtimg.com/cul/pics/hv1/164/82/2191/142490849.jpg&quot; style=&quot;vertical-align:middle;max-width:640px;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-align:center;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;当代艺术家蔡小松&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img alt=&quot;蔡小松：当代水墨是中国崛起最诗意的注解&quot; src=&quot;http://img1.gtimg.com/cul/pics/hv1/205/82/2191/142490890.jpg&quot; style=&quot;vertical-align:middle;max-width:640px;&quot; /&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-align:center;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;蔡小松 《松·感知No.02》 145×240厘米 水墨纸本 2016年&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;3月8日，美国纽约亚洲艺术周的重点就是各大世界级艺术机构举办的展览与活动，而苏富比纽约此番再度将中国当代水墨作为展览主题，蔡小松的水墨新作《沉香山》《瀑界》四联作仍将出现在纽约苏富比总部大楼。这场名为“道殊——同求于墨”的展览，囊括了世界当代水墨艺术界的几位领军人物，而当代水墨又成为世界艺术的焦点。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;艺术家简介&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;蔡小松（1964），中国当代水墨领军人物。出生于上海知识分子家庭，毕业于上海美术学院中国画专业。他早年研习并试验过西方艺术媒材与形式之后，选择笔墨纸砚。自2002年始，蔡小松专注创作受其个人丰富的想象力以及行走启发的奇石画、山水画和大型装置艺术。他的部分作品现藏于国际著名美术馆与重要私人收藏家手中，并于世界各地举办个展及参加群展，包括：西班牙瓦伦西亚当代艺术博物馆（个展）、古巴哈瓦那国家美术馆（个展）、第54届威尼斯双年展、荷兰鹿特丹世界博物馆、北京今日美术馆、上海多伦现代美术馆，以及其他亚洲和欧洲地区重要画廊。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;蔡小松曾经用水墨呈现出卓绝的细节表现能力，甚至能绘出不同石材的细微肌理，令人叹为观止。经过近几年在世界各地的游历与沉潜，他的水墨艺术又呈现出前所未有的新气象。对他来说，中国水墨是中国文脉之正统，其清雅气韵不仅是中国古典美学流传下来的珍宝，更拥有其他艺术形式不能替代的独特价值，是中国艺术家取之不竭的灵感宝库，更是凡俗生活中超然的心灵寄托。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在冲突频仍的全球化背景下，蔡小松连续5年在美国纽约苏富比用当代水墨为中国艺术代言，因为他坚信，进步的当代水墨乃是中国崛起最诗意的注解。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;对我来说，水墨不是回归，而是本源和出发点。从“山石”系列到“感·知”系列，是一个从局部到整体、从微观到宏观的过程，是由小格局到大视野的转变。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;文化财富：在您过去的作品中，“石头”是一个较具代表性的主题，作品中对文人赏石材质细微之处的精妙处理令人叫绝。而在您的新作品中，却彻底放弃了这一题材，为何做出这样的改变？&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;蔡小松：对我来说，石头是山水的分支、是山的浓缩，当时画石头侧重于技术和局部的探讨；如今，我已经跳出对技法的迷恋，这也是从微观到宏观的转向。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在画“山石”系列时，我集中精力研究不同石头材质间的差别，比如太湖石、灵璧石、花岗岩、大理石，讲究时间沉淀在不同材质上的肌理，力图用水墨呈现卓越的技法，这也是非常有价值的阶段。作品美则美矣，但太过具象，缺乏宏观的哲学背景和文化支撑，路会越走越窄，需要“打开”。中国文化有个境界是“混沌”，一种暧昧的不确定性，却蕴含着巨大的包容性和可能性，所以，我放下了一度引以为豪的描摹具体对象的技法。我想创造一个新世界，一种新境界，提供我所感受到的“天地有大美而不言”，于是形成了新系列“感·知”。如果说我的水墨装置《中国地图》是个人创作历程中的第一次灵光闪现，那么，“感·知”系列可以算作第二次。“感”就是感性，“知”就是理性，这一对矛盾旋转式地扶摇而上、更替轮回。我认为，创作应该从哲学层面展开，而解读应该交给观众。画面里不再是山、不再是水、不再是云、不再是树，却又处处有山、处处是树、处处水流、处处云涌。我画我的自我感知，我画我的胸中丘壑，而观者则通过观赏获得个人“感知”。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;文化财富：古代文人在仕宦庙堂之余，往往寄情山水以排遣不遇之悲、迁客之愁，抒发骚人之绪、青云之志。而您的作品，与古代文人画相比有哪些异同？&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;蔡小松：我认为，对水墨的探讨要注重它的时代性。古人游历山川大部分要依靠步行，视野自然会受到局限；而当代我们可以借助科技的力量，在万米高空俯瞰广阔山河，甚至可以看到外太空星球的表面，气度自然不同。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在绘画之外，我热衷于探险，我想我大概是中国最有冒险精神的艺术家了。我曾开着一辆自己改装成简易房车的“金杯”游历中国大西南；我曾徒步翻越高黎贡山，也曾穿越麦克马洪线；曾在新疆达坂城遭遇沙尘暴，在多瀑沟差点送命；我曾花20天自驾穿越撒哈拉沙漠，完成达喀尔拉力赛行程……对自然、对生命，我有自己的体悟与深思，这些对我的创作产生了巨大影响。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;从作品来讲，我决定直接描绘万物的本源，我关注的不再是具体的山水，而是整个时空。齐白石提出“似与不似之间”这一概念极其高明，可他更多停留在“似”的层面。在我看来，“似与不似之间”就是从哲学角度对客观对象进行高度提炼，既非具象也非抽象。中国文人艺术若需抵达最高成就，当不再重复过去画山水那套，而创新式表达自我的高山雄阔，内心的静水深流。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;“当代”是具有进步意义的，但它并不意味着颠覆。当代水墨需要从传统文脉以及异质文明中兼收并蓄，同时也要保持冷静与客观。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;文化财富：近年来，“当代水墨”在中国乃至全世界都成为业内的热点话题，围绕着这一名词也产生了各种争论，对于“当代水墨”，您是如何理解的？或者说，您认为水墨何以当代？&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;蔡小松：当代水墨这个词，重点在于对当代的判断和对水墨的理解。就“当代”来说，我认为，当代意味着进步、发展和开放的精神，并非推翻、打倒和无休止的质问。中国当代艺术经历了前30年的探索，主要是用作品提出问题，后30年应进入为问题寻找答案的阶段。当代艺术这个概念早年主要受西方影响，而我认为对西方当代艺术的理念，同样应保持理性的判断和审慎的态度，冷静地审视自己该怎么走。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;至于“水墨”，古希腊哲学之父泰斯勒认为，世界的本源来源于水，中国人认为“上善若水”；而“墨”则是学养的象征，我们形容一个人有文化，就说他肚子里有墨水。“水墨”一词高度概括出了中国古典艺术的精髓。“水墨”是中国文脉之正统，需要艺术家在尊重的前提下做出创新，对它的态度，不应是重复，也不能是颠覆，最恰当的应是丰富。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;文化财富：水墨发展至今，如何继续前行是艺术家们一直思考、探讨的问题。在您看来，水墨应如何提高其国际化的程度？&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;蔡小松：我认为，艺术的国际化绝不仅是形式的演变，更是精神层面上的探索。从时间轴上分析，要解决好传统与当代的关系，我们要画当代人的水墨，画给当代人欣赏，这其实是文明世界的共同课题，不止我们独有。从空间轴上分析，就是中国与世界的关系。要把自己的作品放在全球化的背景中，平行放置在世界艺术的大格局中去解决人类共同的问题。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;当代水墨的国际化，根源还在于对水墨本体的探索，不应一味从形式上迎合西方审美趣味。从我的作品来说，我曾经创作过水墨装置作品，装置是便于让西方人了解中国水墨的一种形式创新，我是用“洋人的瓶子装中国的酒”，通过这一途径，让西方人第一眼就被中国水墨吸引了。但装置只是瓶子，水墨才是美酒。近期的“感·知”系列，都是围绕着水墨本体进行的创作。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在我看来，艺术是文化最强有力的符号。在中国崛起的大背景之下，我们不能仅满足于做世界最大的物质输出国，更应做价值的提供者，为全球传递东方审美高度。比如，每年3月在美国纽约举办的亚洲艺术周，选择的中国艺术作品都是水墨，我的作品也在其中，这也是中国水墨得到国际认可的例证。中国水墨艺术有辉煌的过去，更有无限未来，我对此充满信心。今年5月还将在苏富比香港举办我的大型个展，希望通过自己的努力，用中国当代水墨为崛起的中国，在世界级平台摇旗呐喊。&lt;/span&gt;\r\n	&lt;/p&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/h1&gt;', '[\"aa\",\"ss\",\"dd\"]', '/uploads/20170306/d8151c82d0dd979eba38124c6deef6aa.jpg', '0', '1488796913', '1');
INSERT INTO `think_document` VALUES ('5', '1', 'test3', '0', '十二届全国人大五次会议在京开幕', '&lt;h1 style=&quot;margin:0px 0px 15px;padding:0px;font-size:26px;font-weight:normal;font-family:&amp;quot;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;新华社北京3月5日电 第十二届全国人民代表大会第五次会议5日上午在人民大会堂开幕。国务院总理李克强向大会作政府工作报告时指出，使命重在担当，实干铸就辉煌。我们要更加紧密地团结在以习近平同志为核心的党中央周围，同心同德，开拓进取，努力完成今年经济社会发展目标任务，为实现“两个一百年”宏伟目标、建设富强民主文明和谐的社会主义现代化国家、实现中华民族伟大复兴的中国梦而不懈奋斗。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img src=&quot;http://inews.gtimg.com/newsapp_bt/0/1217115551/641&quot; style=&quot;vertical-align:middle;max-width:640px;display:block;&quot; /&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;3月5日，第十二届全国人民代表大会第五次会议在北京人民大会堂开幕。党和国家领导人习近平、李克强、俞正声、刘云山、王岐山、张高丽等出席会议。新华社记者 兰红光 摄&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img src=&quot;http://inews.gtimg.com/newsapp_bt/0/1217010147/641&quot; style=&quot;vertical-align:middle;max-width:640px;display:block;&quot; /&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;3月5日，第十二届全国人民代表大会第五次会议在北京人民大会堂开幕。 新华社记者 庞兴雷 摄&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;今天的人民大会堂气氛庄严热烈。万人大礼堂内灯火辉煌，主席台帷幕中央国徽高悬，十面红旗分列两侧。近3000名全国人大代表肩负人民重托出席盛会，认真履行宪法和法律赋予的神圣职权。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;大会主席团常务主席、执行主席张德江主持大会。大会主席团常务主席、执行主席李建国、王胜俊、陈昌智、严隽琪、王晨、沈跃跃、吉炳轩、张平、向巴平措、艾力更·依明巴海、万鄂湘、张宝文、陈竺在主席台执行主席席就座。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;习近平、李克强、俞正声、刘云山、王岐山、张高丽和大会主席团成员在主席台就座。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;十二届全国人大五次会议应出席代表2924人。今天的会议，出席2862人，缺席62人，出席人数符合法定人数。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;上午9时，张德江宣布：中华人民共和国第十二届全国人民代表大会第五次会议开幕。会场全体起立，高唱国歌。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;根据会议议程，国务院总理李克强代表国务院向大会作政府工作报告。报告共分三部分：一、2016年工作回顾；二、2017年工作总体部署；三、2017年重点工作任务。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img src=&quot;http://inews.gtimg.com/newsapp_bt/0/1217148886/641&quot; style=&quot;vertical-align:middle;max-width:640px;display:block;&quot; /&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;3月5日，第十二届全国人民代表大会第五次会议在北京人民大会堂开幕。国务院总理李克强作政府工作报告。 新华社记者 庞兴雷 摄&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强说，过去一年，我国发展面临国内外诸多矛盾叠加、风险隐患交汇的严峻挑战。在以习近平同志为核心的党中央坚强领导下，全国各族人民迎难而上，砥砺前行，推动经济社会持续健康发展。党的十八届六中全会正式明确习近平总书记的核心地位，体现了党和人民的根本利益，对保证党和国家兴旺发达、长治久安，具有十分重大而深远的意义。各地区、各部门不断增强政治意识、大局意识、核心意识、看齐意识，推动全面建成小康社会取得新的重要进展，全面深化改革迈出重大步伐，全面依法治国深入实施，全面从严治党纵深推进，全年经济社会发展主要目标任务圆满完成，“十三五”实现了良好开局。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强从继续创新和加强宏观调控，经济运行保持在合理区间；着力抓好“三去一降一补”，供给结构有所改善；大力深化改革开放，发展活力进一步增强；强化创新引领，新动能快速成长；促进区域城乡协调发展，新的增长极增长带加快形成；加强生态文明建设，绿色发展取得新进展；注重保障和改善民生，人民群众获得感增强；推进政府建设和治理创新，社会保持和谐稳定等方面回顾了过去一年的工作。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强指出，我们也清醒看到，经济社会发展中还存在不少困难和问题。我们一定要直面挑战，敢于担当，全力以赴做好政府工作，不辱历史使命，不负人民重托。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强说，2017年发展的主要预期目标是：国内生产总值增长6.5%左右，在实际工作中争取更好结果；居民消费价格涨幅3%左右；城镇新增就业1100万人以上，城镇登记失业率4.5%以内；进出口回稳向好，国际收支基本平衡；居民收入和经济增长基本同步；单位国内生产总值能耗下降3.4%以上，主要污染物排放量继续下降。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强报告了做好2017年政府工作要把握好的五点：一是贯彻稳中求进工作总基调，保持战略定力；二是坚持以推进供给侧结构性改革为主线；三是适度扩大总需求并提高有效性；四是依靠创新推动新旧动能转换和结构优化升级；五是着力解决人民群众普遍关心的突出问题。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强说，2017年要重点做好九个方面工作：一是用改革的办法深入推进“三去一降一补”；二是深化重要领域和关键环节改革；三是进一步释放国内需求潜力；四是以创新引领实体经济转型升级；五是促进农业稳定发展和农民持续增收；六是积极主动扩大对外开放；七是加大生态环境保护治理力度；八是推进以保障和改善民生为重点的社会建设；九是全面加强政府自身建设。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;李克强还就民族、宗教和侨务工作，国防和军队建设，香港、澳门发展和两岸关系，以及我国外交政策作了阐述。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;根据会议议程，大会审查2016年国民经济和社会发展计划执行情况与2017年国民经济和社会发展计划草案的报告及2017年计划草案、2016年中央和地方预算执行情况与2017年中央和地方预算草案的报告及2017年全国预算草案。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在主席台就座的还有：马凯、王沪宁、刘延东、刘奇葆、许其亮、孙春兰、孙政才、李源潮、汪洋、张春贤、范长龙、孟建柱、赵乐际、胡春华、栗战书、郭金龙、韩正、杜青林、赵洪祝、杨晶、常万全、杨洁篪、郭声琨、王勇、周强、曹建明、韩启德、帕巴拉·格列朗杰、董建华、万钢、林文漪、罗富和、何厚铧、张庆黎、李海峰、陈元、卢展工、周小川、王家瑞、王正伟、马飚、齐续春、陈晓光、马培华、刘晓峰、王钦敏，以及中央军委委员房峰辉、张阳、赵克石、张又侠、吴胜利、马晓天、魏凤和等。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;香港特别行政区行政长官&lt;/span&gt;&lt;a class=&quot;a-tips-Article-QQ&quot; target=&quot;_blank&quot; href=&quot;http://t.qq.com/LeungChunying?pref=qqcom.keyword&quot; style=&quot;text-decoration:none;outline:0px;color:#000000;border-bottom:1px dotted #536DA6;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;梁振英&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;、澳门特别行政区行政长官崔世安列席会议并在主席台就座。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;出席全国政协十二届五次会议的政协委员列席大会。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;中央和国家机关有关部门、解放军及武警部队、各人民团体有关负责人列席或旁听了大会。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p class=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;各国驻华使节旁听了大会。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;span style=&quot;font-size:9px;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/h1&gt;', '[\"\\u4e0d\\u61c8\\u594b\\u6597\",\"\\u540c\\u5fc3\\u540c\\u5fb7\",\"\\u5b9e\\u5e72\\u94f8\\u5c31\\u8f89\\u714c\"]', '/uploads/20170306/815eadad9d03dc376caff4d900c2c41a.jpg', '0', '1489986828', '2');
INSERT INTO `think_document` VALUES ('6', '1', 'test2', '0', '中国的心理咨询开始火了，然后呢？', '&lt;div class=&quot;daoyu&quot; style=&quot;margin:10px 0px;padding:0px;text-indent:5em;position:relative;font-size:16px;line-height:34px;font-family:&amp;quot;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p id=&quot;shareintro&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在中国，心理咨询并不是一个新词，但心理咨询行业却长期不温不火。最近不少媒体突然发现，心理咨询行业火了起来。这个行业因何火爆，势头能否持续，普通人何时能享受到物美价廉的心理咨询服务，成为很多人关心的话题。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;/div&gt;\r\n&lt;div class=&quot;focus&quot; style=&quot;margin:60px 0px 80px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;div class=&quot;tit&quot; style=&quot;margin:15px 0px;padding:0px 0px 0px 15px;&quot;&gt;\r\n		&lt;h3 style=&quot;margin:0px;padding:0px;width:705px;height:20px;text-indent:-999em;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/ydxd_topic.png&amp;quot;&quot;&gt;\r\n			&lt;span style=&quot;font-size:16px;&quot;&gt;要点速读&lt;/span&gt; \r\n		&lt;/h3&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;focus_list&quot; style=&quot;margin:0px;padding:0px;position:relative;overflow:hidden;&quot;&gt;\r\n		&lt;span class=&quot;t focus_top&quot; style=&quot;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/topicallpic.png&amp;quot;display:block;width:6px;height:5px;position:absolute;top:0px;left:0px;z-index:3;&quot;&gt;&lt;/span&gt;&lt;span class=&quot;t focus_bottom&quot; style=&quot;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/topicallpic.png&amp;quot;display:block;width:6px;height:8px;position:absolute;bottom:0px;left:0px;z-index:3;&quot;&gt;&lt;/span&gt;&lt;em style=&quot;width:720px;height:1px;display:block;position:absolute;left:0px;bottom:0px;background-color:#EDEDF5;z-index:2;&quot;&gt;&lt;/em&gt; \r\n		&lt;ul style=&quot;list-style-position:initial;list-style-image:initial;position:relative;overflow:hidden;text-align:center;background-color:#EDEDF5;border-left:6px solid #9A9DB7;&quot;&gt;\r\n			&lt;li data-text=&quot;面对迅速发展的心理咨询业，公众关心的是，何时能享受到物美价廉的心理服务。&quot; class=&quot;inline-block&quot; style=&quot;padding-top:25px;padding-bottom:25px;width:645px;position:relative;display:inline-block;border-bottom:1px solid #969696;text-align:left;&quot;&gt;\r\n				&lt;span style=&quot;text-align:center;display:block;width:28px;height:37px;line-height:30px;position:absolute;left:0px;top:28px;font-size:16px;color:#E5E5ED;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/yaodbg.png&amp;quot;&quot;&gt;1&lt;/span&gt;&lt;a style=&quot;font-size:18px;display:inline-block;line-height:28px;padding-left:50px;cursor:default;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;面对迅速发展的心理咨询业，公众关心的是，何时能享受到物美价廉的心理服务。&lt;/span&gt;&lt;/a&gt; \r\n			&lt;/li&gt;\r\n			&lt;li data-text=&quot;无论从公众福祉角度，还是从投资回报角度考虑，都应该加大对精神卫生领域的投入。&quot; class=&quot;inline-block&quot; style=&quot;padding-top:25px;padding-bottom:25px;width:645px;position:relative;display:inline-block;border-bottom:1px solid #969696;text-align:left;&quot;&gt;\r\n				&lt;span style=&quot;text-align:center;display:block;width:28px;height:37px;line-height:30px;position:absolute;left:0px;top:28px;font-size:16px;color:#E5E5ED;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/yaodbg.png&amp;quot;&quot;&gt;2&lt;/span&gt;&lt;a style=&quot;font-size:18px;display:inline-block;line-height:28px;padding-left:50px;cursor:default;&quot;&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;无论从公众福祉角度，还是从投资回报角度考虑，都应该加大对精神卫生领域的投入。&lt;/span&gt;&lt;/a&gt; \r\n			&lt;/li&gt;\r\n		&lt;/ul&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;有了钱心更烦，中国的心理咨询火了起来&lt;/span&gt; \r\n&lt;/h2&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;最近两年，中国心理咨询行业迎来了一个小高潮。一方面是消费者人数不断增加，另一方面是咨询机构数量增长迅猛——心理咨询平台简单心理联合北京大学心理咨询与治疗中心发布的《2016心理健康认知度与心理咨询行业调查报告》（简称“报告”）显示，传统实体心理咨询机构从（2009年-2012年）200家，增加到（2013年至今）357家；基于互联网的心理咨询产品从2014年前的1家增加到现在的64家。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148867553533172.jpg&quot; alt=&quot;心理咨询机构，尤其是互联网心理咨询机构数量近两年出现井喷式增长。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; title=&quot;心理咨询机构，尤其是互联网心理咨询机构数量近两年出现井喷式增长。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;心理咨询机构，尤其是互联网心理咨询机构数量近两年出现井喷式增长。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;中国心理咨询产业近两年的发展，其来有自。国外的经验表明，在丰衣足食之后，经济继续增长未必能提升人们的幸福感，相反，人们的幸福感会出现停滞或下降，心理疾病的发病率可能提高。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;中国也不例外。一方面，90年代以来经济社会的剧烈变动，让人们的心理受到了前所未有的冲击。世界卫生组织的一份调查显示，中国大概有1.9亿人在一生中需要接受专业的心理咨询或心理治疗。另一方面，随着经济的增长，中国户均可支配收入在5000元-8000元/月的“新兴中间层”和1.2万元-2.2万元/月的“中上层”人群比例不断提升。这些人有能力和意愿为心理咨询付费。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;更重要的是，互联网技术的发展，让心理学科普变得更容易，大量网络心理咨询平台的出现，拉近了患者和心理咨询师的距离。于是，我们看到了心理咨询行业爆发式的增长。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;心理咨询行业继续发展面临的现实困境不小&lt;/span&gt; \r\n&lt;/h2&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;心理咨询行业迎来了春天，但这个春天能否持久，还要看行业发展面临的问题能否得到解决。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;首先，公众对进行心理咨询仍有认知误区。受传统观念的影响，不少人仍把心理问题看成性格软弱或意志力不足的表现，认为只有得了“精神病”，才要看心理医生。这种社会认知，对敢于进行心理咨询的人也有压力，参与调查报告的5000名付费来访者中，有14.4%的来访者会刻意隐瞒自己曾经或者进行咨询的事实。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;其次，是心理咨询的费用问题。由于中国心理咨询尚未被纳入医保，心理咨询费用需要消费者承担。目前，国内一线城市心理咨询师的收费已经达到300-1500/小时，即便如此，很多心理咨询机构，“收入一半给咨询师，剩下的一半贴进硬成本，盈利空间依然有限”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148866755497219.jpg&quot; alt=&quot;为了招揽顾客，很多实体心理咨询机构设在市中心，租金成本很高&quot; title=&quot;为了招揽顾客，很多实体心理咨询机构设在市中心，租金成本很高&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;为了招揽顾客，很多实体心理咨询机构设在市中心，租金成本很高&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;但对普通人来说，这个价格明显太高。2015年，一项针对广州某社区进行的调查显示，超过85%的居民认为心理咨询价格应该在每小时100元以内，认为合理收费为200元以上的仅有2.1%。如果心理咨询的费用依然不纳入医保，也无法降低成本，心理咨询可能会长期处于“叫好不叫座”的局面。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;再次，心理咨询从业者依然良莠不齐。北京大学心理学系教授钱铭怡指出，国际上，心理咨询师首先必须是经过本专业的硕士、博士培训，再进行800—2000小时的实习，才能上岗。 而在中国，按照《心理咨询师国家职业标准》，一个没有任何相关知识背景的本科生，只需交几千块钱培训一段时间，就能考取国家三级心理咨询师执业证书。心理咨询师的上岗培训时间则常不到国际水平的十分之一。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;对看不见摸不着的心理服务，公众本来就对其效果存疑；从业人员如此选拔，只会加重公众的疑虑。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;与“城里人”相比，农村人口对心理咨询的需求更为迫切&lt;/span&gt; \r\n&lt;/h2&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;不少人觉得，心理问题是物质需求得到满足后的“富贵病”，并把它当作“城里人”的专利。实际上，农村人口的心理问题可能更为严重。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;据环球网报道，《柳叶刀》杂志一项针对中印两国精神疾病诊疗状况的研究发现（2016年），中印目前均缺乏专业的精神健康专家，患者就医困难，这种状况在乡村地区尤为突出。在一些地区，出现明显精神失调症状的患者无法得到诊断，更不要说获得相应的心理治疗。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;中国科学院心理研究所《我国心理咨询与心理治疗发展现状、问题与对策》（2016年）则发现，目前中国的发达与落后地区，公众心理健康需求已呈现不同特征。在发达地区，随着居民收入增加，个人主动的心理咨询与治疗服务需求大幅上升。而在贫困地区，居民无力承担心理健康服务费用，甚至因各种原因无法意识到自身心理健康需求，但其心理健康问题往往更加严重，由此衍生的生理疾病、刑事案件等亦更为沉重。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148866913067807.jpg&quot; alt=&quot;愿意付费进行心理咨询的消费者集中于大城市。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; title=&quot;愿意付费进行心理咨询的消费者集中于大城市。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;愿意付费进行心理咨询的消费者集中于大城市。数据来源：《2016心理健康认知度与心理咨询行业调查报告》&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;更严峻的是，目前我国有900多万留守儿童，3000多万流动儿童。有研究显示，早期处于不利环境的儿童，成人后罹患多种心理疾病患的几率高于普通人群。他们的心理健康状况远比城里人需要关注。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148866814920470.jpg&quot; alt=&quot;农村儿童的心理健康问题更需关注&quot; title=&quot;农村儿童的心理健康问题更需关注&quot; style=&quot;vertical-align:middle;margin-bottom:20px;max-width:100%;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin:40px auto;padding:0px;overflow:hidden;position:relative;text-align:center;width:720px;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;农村儿童的心理健康问题更需关注&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;h2 data-page-model=&quot;title&quot; style=&quot;margin:30px 0px 45px;padding:0px 0px 25px 38px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/playvideo.png&amp;quot;font-family:&amp;quot;font-size:24px;font-weight:500;line-height:50px;border-bottom:1px solid #969696;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;增加财政投入，让普通人负担得起心理服务，会有丰厚的回报&lt;/span&gt; \r\n&lt;/h2&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;去年10月，中共中央、国务院印发了《“健康中国2030”规划纲要》。纲要强调，“加强对抑郁症、焦虑症等常见精神障碍和心理行为问题的干预，加大对重点人群心理问题早期发现和及时干预力度”，这无疑是心理咨询行业发展的巨大推力。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;然而，不少人却认为，在人均收入达到发达国家水平之前，心理咨询注定是奢侈品，普通人无法得到。实际上，这种看法有些想当然。《柳叶刀》的相关研究指出，中印两国精神病患得不到所需诊疗的一个重要原因是投入不足——中印两国在精神卫生方面的投入占医疗总预算的比重均不到1%，而美国的投入约占医疗总预算的6%，德国和法国则达10%以上。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;即使不跟发达国家比，中国在精神卫生方面的投入也该提高。世卫组织2014年精神卫生地图集显示，各国政府在精神卫生方面的支出平均占医疗总预算的3%，低收入国家才不足1%，中国2014人均GDP为6995美元，按照世界银行的标准，中国已属于中等偏上收入国家。如果政府能加大投入，将心理咨询纳入医保，负担减轻的消费者会更愿意进行心理咨询，有利于行业继续发展；“富起来”的咨询机构也可以通过网络，为农村人口提供高质量的心理服务，一举多得。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;过去，人们认为加大精神卫生投入，可以增进公民的健康和福祉，减少人口老龄化后认知障碍症的爆发。世界卫生组织2016年的一项最新研究，则给了加大精神卫生投入新的有力论据。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;世卫组织发现，目前一些常见精神病患的数量不断上升，1990年和2013年期间，患有抑郁症、焦虑症或躁郁症的人数上升了近50%，亟需各国加大投入。世卫组织的研究计算了2016-2030年间，36个低收入、中等收入和高收入国家可能发生的相关治疗费用和健康结果。研究认为，以心理咨询和抗抑郁药物为主的扩大治疗费用估计会达到1470亿美元，但所获得的回报会远远超出成本——公民工作能力提升所带来的价值为3990亿美元，身体健康状况的改善还会额外增加3100亿美元的收益。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot; style=&quot;margin-top:0px;margin-bottom:0px;padding:0px 0px 10px;line-height:34px;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;这意味着在精神卫生上投入1美元的投入可获得4倍多的回报。基于此，世卫组织总干事陈冯富珍表示，“现在我们必须找出办法，确保所有男人、妇女和儿童，无论生活在何处，都能真正获得精神卫生服务”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;br /&gt;\r\n&lt;/p&gt;\r\n&lt;div class=&quot;summary&quot; style=&quot;margin:0px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;div class=&quot;summary_tit&quot; style=&quot;margin:30px 0px 0px;padding:0px;width:720px;height:95px;text-align:center;&quot;&gt;\r\n		&lt;h2 style=&quot;margin:0px;padding:0px;background:url(&amp;quot;http://mat1.gtimg.com/www/topicmenu/topic/topicallpic.png&amp;quot;display:inline-block;width:95px;height:95px;&quot;&gt;\r\n		&lt;/h2&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;summary_text&quot; style=&quot;margin:20px 0px 0px;padding:0px;line-height:36px;text-indent:2em;&quot;&gt;\r\n		&lt;p style=&quot;margin-top:0px;margin-bottom:0px;padding:0px;&quot;&gt;\r\n			&lt;span style=&quot;font-size:16px;&quot;&gt;加大精神卫生投入，让普通人也能够负担心理咨询的费用，不是乱花钱，而是回报丰厚的投资。何乐而不为呢？&lt;/span&gt; \r\n		&lt;/p&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;', '[\"qa01\",\"ggf02\",\"bxt123\"]', '', '1488765082', '1488854252', '1');
INSERT INTO `think_document` VALUES ('22', '1', 'test1', '0', '古代孩子的小学生涯 八岁入学读不好会挨揍', '&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;1.古人几岁开始上学&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;现在孩子一般六七岁入学，古代大体在8岁至15岁之间。现代家长忌讳的“八岁八糊涂”的入学年龄，在古代最主流。好多名人都是8岁入学的，如东汉哲学家王充、宋代文学家苏东坡等。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;《大戴礼记·保傅》称，“古者年八岁而出就外舍，学小艺焉，履小节焉”。但《尚书大传·略说》则称“古之帝王者，必立大学、小学……十有三年始入小学”，同篇中还有“十五始入小学，见小节，践小义”的记载。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;可见，先秦对入学年龄无统一要求。明清时期，“大龄入学”传统一直未改变。《嘉靖太平县志》载，明代太平县“令民间子冀盼八岁以上、十五岁以下，皆入社学”。《嘉靖香山县志》则称，有读书潜质的“八岁至十有四者，皆入学”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;而唐代孩子的入学年龄比其他朝代提前一两岁，与现在差不多，即六七岁入学也可以，如唐代医学家孙思邈便是“七岁就学”。据《旧唐书·孙思邈传》，孙思邈虽是7岁入学，但当时已能“日诵千余言”，以至洛州总管独孤信见之大为惊叹，称“此圣童也”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;具体几岁入学，古人也视孩子的心智发育情况而定。甚至20岁读小学的都有。明魏校《庄渠遗书·渝民文》称，“凡为父兄者，如有子弟年六七岁至二十岁未冠者，俱要送入社学。”社学，即地方官府奉朝廷诏令所设的“乡村小学”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;还有30多岁读小学的特例。据《魏书·刘兰传》，北魏人刘兰，便是“年三十余，始入小学”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;strong style=&quot;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt; \r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2.读书不学好会挨揍&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;/strong&gt; \r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古代对学生的管理较严厉，大多家长也认同“不打不成器”。读书不认真或学不好，被打板子、抽鞭子、罚跪如家常便饭。王充《论衡·自纪篇》称，“书馆小僮百人以上，皆以过失袒谪，或以书丑得鞭”。可见，在汉代就流行体罚学生。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;体罚在古代叫“挞罚”。到明代，挞罚为乡村小学普遍采用，连学生家人都跟着受罚。明黄佐《泰泉乡礼·乡校》中规定：“无故而逃学一次，罚诵书二百遍；二次，加朴挞，罚纸十张；三次，挞罚如前，仍罚其父兄。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;当然，也有的老师很人性，给“三好生”学生开“免打条”。明理学家沈鲤就主张，“学生勤学者、有进益者、守学规者，给免帖一纸，遇该责时，姑免一次”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古人还会直接请家长或长者坐进教室，参与班级管理。明代良吏叶春及在惠安办学时即如此，其《石洞集·惠安政书》中这样记载：“轮笃实老成者二人，平旦坐左右塾，以序出入。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古代还很注重对学生日常行为的稽考，以约束学生行为。如明代有的小学设立“扬善簿”、“改过簿”、“记过格”，好事坏事均记录在案，作为学生升学录取时的参考。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;这种“功过簿”并非都由老师填写，如明代儒学家刘宗周，其家塾修业课程中，要求学生早上起来第一事就是填“记过格”，上列数百种日常行为，有“微过”、“隐过”、“显过”、“大过”、“丛过”、“成过”等六项评语，让学生自己评价昨日表现。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;3.怎么考试如何打分&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;strong&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;strong style=&quot;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;&lt;/strong&gt; \r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古代小学主要是识字、写字、习经史、学六艺。据《宋会要·崇儒》，宋代国子监小学“条制”要求：“小学生八岁能诵一大经，日书字二百”；“十岁加一大经、字一百”；“十二岁以上，又加一大经、字二百”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古代检查学生的学业，也靠考试，俗话说“小考天天有，大考三六九”，在古代还真有。如宋代，老师会逐日测试学生的学习，这叫“日考”；另外还有“月考”、“季考”等。具体考试时间，各朝代、各学校都不同。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;元代的上元县（今南京市内），便对小学考试时间作出具体规定。据《庙学典礼·行省坐下监察御史申明学校规式》，上元小学的考试，固定在每月的初三、十六两天。而且，出题和监考要分两班人。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;明代又有不同，明代理学家沈鲤称：“朔望日考试，分等第，行赏罚。”但不同的老师，对考试的要求并不相同。儒学家刘宗周便主张“三六九会课，以二题为率”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;到清代，小学考试形成了制度，根据教学方式与内容的不同，采取不同的考试：公课、月课一月一考，朔望课半月一考，季课一年四考。另有会课的多次考，义学的抽考等等。若重要考试考砸了，还允许“补试”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古代考试评分方式较丰富，有“十分制”、“打勾制”、“评语制”等，但无“百分制”。以“打勾制”来说，优秀的打○，一般的打△，差的打×。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;4.教育不公现象也严重&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;现代的“快慢班”，古代也有。如宋徽宗政和四年（1114年）十二月，颁小学条制，国子监实行“三舍升补法”，班级分“外舍”、“内舍”、“上舍”三种。新生皆分在外舍，成绩好的升入内舍；内舍生考得好的，升入上舍。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;实际上，这种快慢班，更有“留级”和“升级”的味道。“三舍法”一度成为当时全国小学的模式。这种对小学生进行分等的做法，很不合理，遭到反对，并没存在多久。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古代没有中学，小学一般是“七年制”、“八年制”或“十年制”，最短的也要三年。所以，古代不是“小升初”，而是“小升大”，小学读完直接升入太学、国子监一类的高等学府。因此，13岁上大学在古代一点也不稀奇。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;但并非每个小学生都能“小升大”的，乡村小学生就不可能。即便是在国子监读书的“官二代”，也有名额限制，如宋代便将大学的升学率控制在50%。《宋史·选举志三》载，熙宁十年（1077年）推出面向宗室学生的“宗子试法”，规定“十取其五”。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;古代教育不公现象也很严重，宋代以后，教育不公问题受到重视。元明清三代，小学教育走向兴盛，特别是元代，大力推广乡村小学，要求“遍立学校”，五十为一社，“每社立学校一”，农村孩子受教育的机会大增。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;明清时期，由地方官府或慈善人士开办的义学（义塾），得到进一步发展。义学是免费的，解决穷苦人家孩子的“上学难”问题，这可视为古代的“希望小学”。（文/倪方六）&lt;/span&gt; \r\n&lt;/p&gt;', '[\"\\u53e4\\u4ee3\\u6559\\u80b2\",\"\\u5c0f\\u5347\\u521d\",\"\\u5c0f\\u5347\\u5927\"]', '/uploads/20170306/d3d440741134c62086c2b2585c20ccc1.jpg', '1488797889', '1488797962', '1');
INSERT INTO `think_document` VALUES ('23', '1', 'test2', '0', '广州百姓堂', '&lt;h1 itemprop=&quot;headline&quot; id=&quot;artical_topic&quot;&gt;\r\n	韩国检方：朴槿惠和崔顺实向三星索贿430亿\r\n&lt;/h1&gt;\r\n&lt;div id=&quot;artical_sth&quot; class=&quot;clearfix&quot;&gt;\r\n	&lt;p class=&quot;p_time&quot;&gt;\r\n		&lt;span itemprop=&quot;datePublished&quot; class=&quot;ss01&quot;&gt;2017年03月06日 13:49:14&lt;/span&gt;&lt;br /&gt;\r\n&lt;span class=&quot;ss02&quot;&gt;来源：&lt;/span&gt;&lt;span itemprop=&quot;publisher&quot; itemscope=&quot;&quot; itemtype=&quot;http://schema.org/Organization&quot;&gt;&lt;span itemprop=&quot;name&quot; class=&quot;ss03&quot;&gt;&lt;a href=&quot;http://news.haiwainet.cn/n/2017/0306/c3541093-30774364.html&quot; itemprop=&quot;isBasedOnUrl&quot; target=&quot;_blank&quot;&gt;海外网&lt;/a&gt;&lt;/span&gt;&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;div class=&quot;share ipad_block&quot; style=&quot;z-index:1000;&quot;&gt;\r\n		&lt;div style=&quot;width:232px;height:0px;position:absolute;left:-37px;bottom:30px;z-index:103;overflow:hidden;&quot; class=&quot;js_weixin&quot;&gt;\r\n			&lt;div class=&quot;ts_wx&quot; style=&quot;background:url(http://y0.ifengimg.com/commonpage/0304/arrow.gif);height:112px;cursor:auto;z-index:1000;&quot;&gt;\r\n				&lt;div style=&quot;position:relative;padding:6px;z-index:1000;height:100%;width:100%;&quot;&gt;\r\n					&lt;img src=&quot;http://h2.ifengimg.com/0f56ee67a4c375c2/2013/1106/indeccode.png&quot; style=&quot;width:86px;height:86px;float:left;margin-right:14px;&quot; class=&quot;js_wx_qrcod&quot; /&gt; \r\n					&lt;p style=&quot;line-height:24px;color:#666;font-size:12px;width:auto;padding-top:12px;&quot;&gt;\r\n						用微信扫描二维码&lt;br /&gt;\r\n分享至好友和朋友圈\r\n					&lt;/p&gt;\r\n				&lt;/div&gt;\r\n			&lt;/div&gt;\r\n		&lt;/div&gt;\r\n		&lt;div class=&quot;bdshare_t bds_tools get-codes-bdshare share-bar-btn1&quot;&gt;\r\n		&lt;/div&gt;\r\n	&lt;/div&gt;\r\n	&lt;div class=&quot;box03&quot; id=&quot;js_comment_box03&quot; style=&quot;display:block;&quot;&gt;\r\n		&lt;h5 class=&quot;cmt js_commentCount&quot; id=&quot;js_commentCount_top&quot;&gt;\r\n			&lt;span class=&quot;ss_none&quot;&gt; &lt;a href=&quot;http://gentie.ifeng.com/view.html?docUrl=http%3A%2F%2Fnews.ifeng.com%2Fa%2F20170306%2F50756245_0.shtml&amp;amp;docName=%E9%9F%A9%E5%9B%BD%E6%A3%80%E6%96%B9%EF%BC%9A%E6%9C%B4%E6%A7%BF%E6%83%A0%E5%92%8C%E5%B4%94%E9%A1%BA%E5%AE%9E%E5%90%91%E4%B8%89%E6%98%9F%E7%B4%A2%E8%B4%BF430%E4%BA%BF&amp;amp;skey=0c9828&amp;amp;pcUrl=http://news.ifeng.com/a/20170306/50756245_0.shtml&quot; target=&quot;_blank&quot; class=&quot;js_joinUrl js_peopleNumber&quot; id=&quot;&quot;&gt;&lt;em class=&quot;js_joinNum&quot;&gt;2100&lt;/em&gt;&lt;span&gt;人参与&lt;/span&gt;&lt;/a&gt; &lt;/span&gt; &lt;a href=&quot;http://gentie.ifeng.com/view.html?docUrl=http%3A%2F%2Fnews.ifeng.com%2Fa%2F20170306%2F50756245_0.shtml&amp;amp;docName=%E9%9F%A9%E5%9B%BD%E6%A3%80%E6%96%B9%EF%BC%9A%E6%9C%B4%E6%A7%BF%E6%83%A0%E5%92%8C%E5%B4%94%E9%A1%BA%E5%AE%9E%E5%90%91%E4%B8%89%E6%98%9F%E7%B4%A2%E8%B4%BF430%E4%BA%BF&amp;amp;skey=0c9828&amp;amp;pcUrl=http://news.ifeng.com/a/20170306/50756245_0.shtml&quot; target=&quot;_blank&quot; class=&quot;js_cmtUrl js_commentNumber&quot; id=&quot;&quot;&gt;&lt;em class=&quot;js_cmtNum&quot;&gt;305&lt;/em&gt;&lt;span class=&quot;ss_none&quot;&gt;评论&lt;/span&gt;&lt;/a&gt; \r\n		&lt;/h5&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p class=&quot;detailPic&quot;&gt;\r\n	&lt;img src=&quot;http://p3.ifengimg.com/a/2017_10/f866a78ff4d90d5_size115_w534_h396.jpg&quot; alt=&quot;&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	原标题：特检组:朴槿惠和崔顺实共谋 从三星受贿430亿韩元\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	海外网3月6日电 3月6日下午，韩国“亲信门”特别检察组在位于首尔江南区的特检组办公室召开发布会，特别检察官朴英洙公布“亲信门”调查结果。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	朴英洙表示，朴槿惠和崔顺实共谋，从三星集团收取430亿韩元（约合人民币2.5亿元）的贿赂。2015年朴槿惠指示三星物产和第一毛织合并，以支持李在镕顺利当选三星电子副会长，作为回报三星集团向崔顺实及米勒财团、K\r\n sports财团及韩国冬季运动会英才中心行贿430亿韩元。崔顺实的财产包括本人的228亿韩元在内，共计2700亿韩元。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	朴英洙表示，确定朴槿惠总统涉嫌行贿和制作文化界黑名单，并确定朴槿惠参与秘密诊疗。朴槿惠在崔顺实的介绍下，接受过几位非法医疗机构人员的微整手术。这显示国家元首健康管理的青瓦台医疗机构处于崩溃状态。\r\n&lt;/p&gt;\r\n&lt;div id=&quot;embed_hzh_div&quot; class=&quot;embedHzh&quot; style=&quot;width:300px;height:250px;margin:10px 20px 10px 0px;float:left;display:inline;overflow:hidden;clear:both;padding:4px;border:1px solid #CDCDCD;&quot;&gt;\r\n	&lt;div id=&quot;J_huaz&quot; style=&quot;position:relative;margin-bottom:4px;&quot;&gt;\r\n	&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	朴英洙表示，朴槿惠和崔顺实通过借名手机，在2016年4月18日到10月26日之间共计通话573次。特别是亲信干政门事件曝光以后，崔顺实逃往德国后从去年9月3日到10月30日接受检方调查回国为止，与朴槿惠共计通话127次。据悉，朴槿惠、崔顺实等人使用的借名手机，是前青瓦台行政官李英善2013年10月到去年10月之间，在在京畿道富川市一个营业代理点办理的，共计开设52部。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	朴英洙表示，特检组进行了46次搜索调查，由于时间有限以及调查对象不配合，2月28日调查结束时，调查只进行了一半。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	去年11月，深陷“闺蜜干政门”的韩国总统朴槿惠表态，称愿意接受特别检察组的调查。随后，韩国国会表决通过设立特检组，由20名检察官组成，去年12月21日正式成立，获国会授权进行为期70天的调查。随后，特检组认定韩国总统朴槿惠为涉腐嫌疑人，以收受贿赂的嫌疑对朴槿惠立案。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	2月28日调查期满之际，特检组共起诉了30名嫌疑人，其中最受关注的，无疑是总统闺蜜崔顺实和三星副会长李在镕。除此之外，还包括三星集团的多名高层、多名前青瓦台要员，以及涉嫌在“世越”号船难当天为朴槿惠实施整容注射的医生等人。(编译/刘强)&lt;span class=&quot;ifengLogo&quot;&gt;&lt;a href=&quot;http://www.ifeng.com/&quot; target=&quot;_blank&quot;&gt;&lt;img src=&quot;http://p2.ifengimg.com/a/2016/0810/204c433878d5cf9size1_w16_h16.png&quot; /&gt;&lt;/a&gt;&lt;/span&gt;\r\n&lt;/p&gt;', '[\"a123\",\"bxt123\",\"axt1234\"]', '/uploads/20170306/9f5fa8134d85b7844e1db13c76076212.jpg', '1488798235', '1488871985', '1');
INSERT INTO `think_document` VALUES ('10', '1', 'test3', '0', '吕克·费里：当爱从私人领域延伸到公共领域会怎样', '&lt;h1 style=&quot;margin:0px 0px 15px;padding:0px;font-size:26px;font-weight:normal;font-family:&amp;quot;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;“当我们看到前总理突然为儿子换了住房，因为对巴黎城区公寓房持有权力，我不会向他投掷石块，而只是直接问他：那其他人呢？”&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;说这句话的是法国前教育部长、哲学家吕克·费里。1968年发生在法国的那场“五月风暴”深深影响了费里的一生。美国作家马克·科兰斯基在其《1968年：撞击世界之年》中，将“五月风暴”描述为一场为了反对而反对、为了革命而革命的极端事件。法国社会学泰斗雷蒙·阿隆称其为一出无聊的“心理闹剧”：年轻人根本不知道为了什么而抗议。著名历史学家托尼·朱特则认为，这场年轻人“鄙视并憎恶消费文化的运动从一开始就成了文化消费的目标”。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img alt=&quot;吕克·费里：当爱从私人领域延伸到公共领域会怎样&quot; src=&quot;http://img1.gtimg.com/cul/pics/hv1/22/72/2191/142488157.jpg&quot; style=&quot;vertical-align:middle;max-width:640px;&quot; /&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-align:center;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;吕克·费里 资料图&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;当今反对全球化的浪潮实实在在源自经济发展和财富分配的不平等。这种不平等是一种亟待解决的社会现象，并非像“五月风暴”那样属于“无病呻吟”。然而，在费里看来，无论是“五月风暴”还是反对全球化，两者都具有某种共性：都是现代性自身开出的“恶之花”。现代社会比以往任何时代都赋予了个体更多的自由，但这种自由一旦推到极端，不受到某些原则的约束，就会释放出各种虚无主义、相对主义和极端信念，从而俘获对现状失望的人的心灵，成为他们反抗现实的思想武器。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;费里深知现代人道主义（他避免使用“自由主义”，因为自由主义谱系的两端分别连着虚无主义和极权主义）的困境，但与后现代主义者和神学家不同，他既不愿意诉诸解构的方法，让现代社会成为虚无主义所主宰的世界，也不愿意退回到宗教信仰，让历史上曾引发过无数暴力流血事件的绝对信念在现代社会引起新的压抑和冲突。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img alt=&quot;吕克·费里：当爱从私人领域延伸到公共领域会怎样&quot; src=&quot;http://img1.gtimg.com/cul/pics/hv1/47/72/2191/142488182.jpg&quot; style=&quot;vertical-align:middle;max-width:640px;&quot; /&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-align:center;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;《论爱》&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;面对现代性困境，费里没有否认个体自由的意义，也没有跳出现代性本身的框架，而是试图在框架内找到能够驾驭“危险的自由”的原则。在《论爱》这本新书中，费里提出了一个新的生活意义原则，也是他所谓的第二次人道主义革命的核心原则——爱。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;自由之爱的重要意义&lt;/span&gt;&lt;/strong&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;费里的“爱”与基督教的“爱”在内涵上有一定的重叠，但在根本上两者的旨趣又完全不同。费里想要的是哲学的爱，而不是信仰的爱。自尼采以降，现代哲学越来越专业化，试图获得与科学齐平的学科地位。而在古希腊时代，哲学大量探讨的则是生命意义和伦理规范，它们后来都成为启蒙运动的思想养料。费里并不完全拒斥哲学研究的专业化，但他认为要解决现代性困境，就必须把焦点重新放在探究人的意义原则的哲学上，而这种“哲学是没有上帝的关于拯救的学说，是对生活意义的探求，这种探求……凭借理性的唯一明晰性，而不求助于永恒不朽。这种追求主张世俗精神。从定义上讲潜入我们的所有机制中的爱，将在协调一致的哲学体系内部，以无与伦比的力量把我们的每一种活动和某种意义原则联系起来”。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;不寻求超越于人的来自至高力量的爱，就把人放在了自由平等的主体位置上，而这正是第一次人道主义革命——也即启蒙运动所带来的积极成果，人类社会由此第一次摆脱了超越性力量的桎梏，开始发现和欣赏自身的可能性。正如康德对启蒙的定义，“人类从自我造成的不成熟状态中解脱了出来”。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;费里的论证正是从“爱”最狭义的内涵开始的。他简要回顾了人类的爱情婚恋史，发现启蒙运动之后，西方社会才逐渐从包办婚姻或门第婚姻走向了自由恋爱和自由婚姻。在此之前，人们很难想象两个异教徒可以结合在一起，也很难想象贵族和平民可以自由婚娶。基于情欲的婚恋是危险的，蒙田就曾表达过类似的意思：娶因情欲、色情而爱上的女人，意味着绝对的灾难！但在人道主义革命之后，个人被看作自由平等的主体。反映在爱的领域，也就让两情相悦第一次成为了人类爱情和婚姻的基础，纯粹的爱情变得比以往任何时代都更具有了现实的可能性。个人全面掌管了属于自己的爱情，摆脱了被父母决定、被身份决定、被宗教教义决定的爱情生活。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;虽然世俗的爱情和婚姻更加脆弱，但人们并没有完全失去对婚姻的掌控，人们不需要回到天主教和基督教对婚姻的教义约束，通过将婚姻神圣化来对抗离婚的世俗诱惑，人们可以将对爱情和婚姻的理解从感性的情欲上升为理性的关爱和家庭的责任，斩断情欲与婚姻生活的狭隘关联。情欲只是婚姻的基础，但不是婚姻生活的全部倚靠，包容、关爱和责任仍是费里所谓的爱的原则的应有之义，是自由个体在自由婚姻中应有的自我修炼，只不过这种修炼并不来自于外在的要求和强迫，而来自于个人对于婚姻生活本质的内在反思。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在费里看来，自由爱情和婚姻具有伟大的历史意义，它既肯定了个人的自主性，尊重了个人的选择，又让基于自愿选择的爱情的结晶——子女后代——在父母眼里有了全然不同的意义。今天，父母对孩子的那种无私的爱，在历史上并非是理所当然的。比如，在中世纪，孩子往往成为非自愿婚姻的牺牲品——孩子被父母遗弃的现象普遍存在。而在现代社会，即便父母离了婚，无论哪一方都不会将婚姻的怨气发泄到孩子身上。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;毫无疑问，在现代人看来，孩子已经成为父母生命意义的延续，父母对孩子的爱超越了父母对自身利益的狭隘关注。某种程度上，这种爱具有永恒的超越性的意义，而这种超越性与超越于人的至高力量无关，与神圣无关，仍属于自由的个体在世俗社会对生命意义的更高追求。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;总之，费里通过肯定自由爱情和婚姻，肯定了第一次人道主义革命（启蒙运动）。由此可知，费里并不是现代性的反对者。但费里很清楚，第一次人道主义革命并不彻底，自由被误读、被滥用。如果没有爱的原则作为约束，自由将走向爱的对立面，陷入现代性纷争的泥沼。因此，费里对爱的原则的阐释就不能仅仅停留在私人爱情的范畴，爱的原则还必须能够解决出现在公共生活中的现代性问题。在费里看来，正是父母对后代的关爱以及由此对后代生活环境的关注，让人们的关注点从狭隘的私人领域转向了宽广的公共领域。而爱的原则既包括对个人和孩子的爱，也包括对他人的爱，这就是他所谓的第二次人道主义革命。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;相对主义的幽灵&lt;/span&gt;&lt;/strong&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;当父母审视孩子所生活的现代社会，他们会发现，第一次人道主义革命所带来的人性自由，一方面确实为个人潜能的实现提供了前所未有的可能性，也为人类社会开启了价值和文化多元的时代，但另一方面，自由一旦没有了“锚”，过多的可能性和多元化也会带来价值的混乱。虚无主义、相对主义和极权主义就是自由社会价值混乱的结果。虽然费里承认，尼采、海德格尔、德里达的后现代解构有其积极意义，让个体之间呈现出更明显的相异性，进一步拓宽了个人潜能的可能性。然而，另一方面，费里却并不认为，所谓的后现代真正解构从而替代了现代性。现代性困境仍然摆在那里，解构只不过进一步加剧了已然十分混乱的价值体系。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;既然在解构者看来，虚无主义、相对主义和极权主义都有其存在的合理性，打着国家、自由解放和革命旗号的极端运动当然也就有了正当性。费里对于这些运动深恶痛绝，与后现代主义者或宗教分子的看法不同，虽然他深知现代性的病症，但他决不会将人道主义解构为虚无主义和相对主义，也决不认同试图退回到前现代社会的“历史怀旧病”。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;明辨文明价值的高低，意味着费里明确肯定了现代社会的先进性及其崇尚的人道主义价值观，他试图用这样的价值观将现代社会从后现代的解构和历史的怀旧中拯救出来。同样基于这种价值观，尽管费里认为不同文明的价值有高有低，但他决不同意为了同化低级文明而对其诉诸武力。费里的爱的原则始终以个体的生命和权利为根基，因此，他所崇尚的第二次人道主义革命并没有脱离对个体命运的关注，他反对为了某种极端意识形态、为了某个群体或个人的私利而牺牲他人的生命和权利。因此，无论是对虚无主义还是极权主义，费里都极为警惕。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;爱他人即是爱自己&lt;/span&gt;&lt;/strong&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;爱的原则一旦从私人领域延伸到公共领域，意味着我们应该像爱自己和自己的孩子那样爱他人，而非仅仅停留于自爱，甚至为了一己之私或一己之念牺牲他人。费里的爱的原则之所以反复强调父母关爱后代的重要意义，是因为这种关爱，正是超越一己之私的起点，进而将关爱投射到更为广泛的他者，或者说，投射到公共领域，而这种投射之所以可能，是因为人有同情心。同情心能够既让人对异质的他者保持宽容，也让人意识到，为他者争取权益就是在为自己争取权益。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;当费里说，他不会向前总理扔石块，而是会质问他，那其他人呢？他就已经很清楚地表达了爱的原则何以能够将私人领域与公共领域嫁接起来：爱决不仅仅是宽容，甚至是顺从，为自己和他人伸张公平正义也是爱的原则的应有之义。这就好比，爱孩子决不意味着宠爱和溺爱孩子，而是应该像费里在书中所强调的那样，父母要对孩子的行为施加必要的戒律，老师要向孩子传授正确的知识。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;费里认为，博爱和同情是爱的原则派生到了公共领域，是现代社会的“爱的政治”。父母一旦关爱后代，意味着父母将不得不关心后代所生存的环境，比如，生态环保、医疗教育、财富分配、经济机会、权利保护等等，这就将他们对自身的关切延伸到了对社会和他者的关切。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在费里看来，爱的原则不仅是解决现代性病症的一剂良药，还为个人提供了生活的意义原则：爱既是现代社会的基石，保障了个体的自由，繁荣了文化的多样性，又为个人生活找到了更广阔而深邃的意义，一种超越了自私自利、更有恒长价值的生活意义——为了后代，为了他人。即使每个人都面临死亡的结局，也无法抹掉此世为了爱的价值而奋斗的意义，这就像一部伟大的小说，作者也许已作古成灰，但小说的精神价值却能恒久光照后来者的心灵。费里相信，这些意义都不需要超越于人的至高力量来赋予，世俗社会完全可以有爱，或者毋宁说，爱本身就是世俗的。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;诚然，基于博爱和同情的爱的原则为世俗的现代生活提供了一种更开放、更广阔的视角，它要求人们从长远的、他者的角度来思考公共问题，这无疑具有重要的现实意义。不过，费里自己也谦逊地承认，第二次人道主义革命（爱的原则）也是一种乌托邦，只不过“这种乌托邦可能是第一次没有被新的灾难充斥、膨胀。因为，（这种）乌托邦意欲实现的理想不是国家的、革命的理想……而是为了我们最爱的人们，即新生代的未来。”&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;的确，仅凭爱的原则无法根本解决现代性的困境。想想看：费里的绝对命令该如何适用于IS这样的极端信徒？他们根本不珍视自己的生命，甚至也不珍视孩子的生命！我们该如何说服被全球化的进程所伤害的美国底层白人，让他们明白，短视的孤立主义最终会损害他们的长远利益？我们该如何说服政府官员，如果不能让某些既得利益者经受短期的阵痛，长期来看，所有人都会因为改革的迟滞而受到更大的伤害？这些问题远非爱的原则能够提供满意的答案，而突围的希望仍要寄托在现代社会的理性慎思和认知进步上。（文/王培）&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/h1&gt;', '[\"\\u5415\\u514b\\u00b7\\u8d39\\u91cc\",\"\\u4e94\\u6708\\u98ce\\u66b4\",\"\\u5fc3\\u7406\\u95f9\\u5267\"]', '/uploads/20170306/10c264577e5dd080e4afcbf56f80400b.jpg', '1488769429', '1488796857', '1');
INSERT INTO `think_document` VALUES ('11', '1', 'test3', '0', '从科幻小说中了解下语言学吧', '&lt;h1 style=&quot;margin:0px 0px 15px;padding:0px;font-size:26px;font-weight:normal;font-family:&amp;quot;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img alt=&quot;从科幻小说中了解下语言学吧 &quot; src=&quot;http://img1.gtimg.com/cul/pics/hv1/5/228/2188/142332845.jpg&quot; style=&quot;vertical-align:middle;max-width:640px;&quot; /&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;科幻电影千年陪跑，从未获得奥斯卡最佳影片。近年来倒是总能跻身提名名单，比如2014年的《地心引力》、去年的《火星救援》和今年的《降临》。《降临》根据华裔作家特德·姜《你一生的故事》改编，影片女主角艾米·亚当斯扮演了身为语言学家的主人公路易斯·班克斯一角。特德·姜的这部科幻小说，灵感来源于一个朋友在生了女儿之后对他的讲述。从构思到完成这篇只有6页纸的短篇小说，特德·姜却花了大约5年时间。他在西雅图为微软公司做科技写手之余，5年中阅读了大量语言学的书籍。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;49岁的特德·姜头发灰白，总是留着马尾辫。他出生在美国长岛，从高中时开始写科幻，一直到在布朗大学学习计算机科学，都坚持不懈地向杂志投稿，却从未得以发表。直到1990年，美国老牌科普杂志《OMNI》第一次发表了他的作品《巴比伦塔》。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;特德·姜至今创作了14部短篇小说和一部中篇小说，他把自己称为“间歇性作家”：“我不想因为谋生而强迫自己去写小说，现在非常开心能够按照自己的步调写短篇。”特德·姜的这些短篇却为他赢得了一长串的科幻奖项。包括雨果奖、星云奖、轨迹奖在内，他至今赢得了27个科幻奖项的认可。本可以有第28个，当年他觉得小说《赏心悦目》是赶稿之作，谢绝了雨果奖的提名。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;《降临》这部电影强行加入不少中国戏份。电影中七肢桶写出的文字被认为有水墨画风、草书神韵，实际上是导演丹尼斯·维纶纽瓦从咖啡杯水印中得到的灵感。小说中的路易斯·班克斯并没有迫在眉睫的国际危机需要应对，也没有通晓汉语的才能。特德·姜在自身创作中一直避免被贴上“华裔作家”的标签，他不会中文，自陈小时候因喜欢一档DC漫画出品的卡通节目《超级朋友》，而中文课程正好和卡通时间冲突，由此学得不爽。特德·姜的科幻作品中一再表现出对语言和语言学的热情，不过对于中国文字的传统，却有所偏见。去年5月他在《纽约客》上发表了一篇观点粗暴的短文——《坏汉字》，言称“我喜欢识文断字，而中国文字千年来都是识文断字的阻碍”。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;在《你一生的故事》中，特德·姜创造了一套外星语言——七文。七肢桶的书面语言和口头语言是完全独立的两套系统，它们说话“像小狗甩掉身上的水”，而它们的书面文字像一堆纠缠混杂的小画。以一位语言学家为主角是小说的特别之处。露易丝在研究“七文”的过程中，习得了七肢桶的思维方式，拥有了“未来50年的记忆”。小说中这种语言决定思维的论调，受到了语言学上颇有争议的“萨丕尔-沃尔夫假说”的影响。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n		&lt;img alt=&quot;从科幻小说中了解下语言学吧 &quot; src=&quot;http://img1.gtimg.com/cul/pics/hv1/29/228/2188/142332869.jpg&quot; style=&quot;vertical-align:middle;max-width:640px;&quot; /&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;科幻作品有着对外星语言的丰富想象。1830年英国作家珀西·格雷格在科幻小说《穿越十二宫》中创造了一套火星文，被认为是第一部创造外星语言的文学作品，同时小说中也为英语提供了一个新词汇“Astronaut（宇航员）”。美剧《星际迷航》中的克林贡语、DC漫画中的氪星语、电影《阿凡达》中的纳威语、日本作家森刚浩之的科幻小说《星界纹章》中的亚威语，这些都成为了外星人文化中的代表。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;当然，和外星人沟通不一定非要靠学“外文”。数字或许可以在宇宙通用。1960年荷兰数学家汉斯·弗罗登塞尔设计了一套宇宙语系统，他认为和外星人沟通可以从基本的数学概念做起，1977年罗伯特·泽米斯基导演的电影《超时空接触》，电影中接收到的无线电信号就是汉斯·弗罗登塞尔的宇宙语。朱迪·福斯特在电影《超时空接触》中扮演了女科学家艾丽，那句“宇宙中如果只有我们的话，那岂不是太浪费地方了”已成经典台词。电影根据美国科普作家卡尔·萨根唯一一部小说改编，拍摄之时卡尔·萨根因病去世。卡尔·萨根同样认为数学是最好的星际通用语言，1974年他参与创作了一组无线电信号，共有1679个二进制数字，通过阿雷西博射电望远镜发向距离地球25000光年的球状星团M13。在《你一生的故事》中，数学家和物理学家和七肢桶建立联系的努力却宣告失败。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;当然，科幻作品还会设计各种各样的设备仪器来和外星人沟通，比如英剧《神秘博士》中时间机器塔迪斯上的翻译矩阵或是《星际迷航》中的宇宙翻译器。道格拉斯·亚当斯在小说《银河系搭车客指南》中创造了一种能够接收脑电波的“巴别鱼”，把一条巴别鱼塞进耳朵，就能立刻理解一切外星语言。当然，像斯皮尔伯格执导的电影《外星人E.T.》中，人类和外星人手指碰一碰就能达到心灵沟通是最美好的想象。不过怎么能保证外星生命都有手指头？&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;音乐也是常见设定。2008年2月4日，美国宇航局在成立50周年之际，向距离地球431光年的北极星发送了披头士乐队的歌曲《穿越苍穹》，期望通过音乐与外星文明产生共鸣。刘慈欣在小说《欢乐颂》中想到的是这首贝多芬的名曲。日本科幻动画《超时空要塞》第一季中，外星人和地球在女主角林明美的歌声中达成了和解，而电影《火星人玩转地球》则把音乐当成了战胜外星人的秘密武器。&lt;/span&gt; \r\n	&lt;/p&gt;\r\n	&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n		&lt;span style=&quot;font-size:16px;&quot;&gt;小说《你一生的故事》中，官方对路易斯千叮咛万嘱咐，在和七肢桶沟通的过程中务必少说地球的事情，电影中七肢桶声称来地球是为了彼此拯救，而在小说中则完全不知它们为何而来。和外星人建立交流未必就有美好前景。刘慈欣在《三体》中设定的“黑暗森林法则”代表了与外星文明沟通的悲观立场：宇宙就是一座黑暗森林，每个文明都是带枪的猎人，如果先发现了对方，最好的办法不是沟通，而是开枪。当然在另一部刘慈欣小说《乡村教师》中，外星舰队的最高执政官还是被孤独的地球文明所感动了，但愿如此。 （文/沈沣）&lt;/span&gt; \r\n	&lt;/p&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;br /&gt;\r\n&lt;/h1&gt;', '[\"duffzhang\",\"\\u4f60\\u4e00\\u751f\\u7684\\u6545\\u4e8b\",\"\\u5218\\u6148\\u6b23\"]', '/uploads/20170320/c023e1adbf50c181ff5f19ffdca9327d.jpg', '1488769458', '1489986543', '1');
INSERT INTO `think_document` VALUES ('12', '2', 'test1', '0', '中国老虎是怎样走上绝路的', '&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“为了贯彻毛主席的指示，保护群众的生命、财产，坚决消灭兽患，特决定在全省范围内，展开一个群众性的打虎运动。要求在今冬明春完成捕打老虎野猪二万只的任务。具体分配如下……”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;这是1956 年江西省人民委员会及军区联合下达的灭虎公告，刊登在了当时的《江西政报》上。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148877502480947.jpg&quot; alt=&quot;1956年贵州榕江县的打虎队&quot; title=&quot;1956年贵州榕江县的打虎队&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;1956年贵州榕江县的打虎队&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;h2 data-page-model=&quot;title&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;一&lt;/span&gt;\r\n&lt;/h2&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;江西原本有很多老虎。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;据考古出土的动物化石，几十万年前的江西人就开始和老虎打交道了。乐平县山下溶洞遗址和萍乡竹山园洞遗址里都发现过虎骨或虎齿。但说到文字记载，直到三国孙吴时期才有一条“五月，鄱阳言白虎仁”。唐以前江西虎的形象大多是这种“白虎”之类的祥瑞，最有名的当是“虎溪三笑”里的那只庐山虎。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;三国以前缺乏老虎的记载，是因为那时候的老虎少吗？事实正好相反。回顾江西老虎记载的逐渐增多，就能发现其与人类的开发范围和强度呈正相关。东汉末年以来北方战乱频仍，大批流民和士人南下，加快了江西的开发进程。那时谶纬之学兴盛，人与老虎又接触不多，所以白虎就经常作为“德政”的代表出现了。这时候的人和老虎，相对来说还是和平相处的。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;这种现象在唐以后发生了改变。安史之乱后，前往江西移居的老百姓越来越多，没有了栖息地的老虎开始袭击人类，人与虎的矛盾便逐渐凸显出来了。如《宋史》就记载过分宁县的一位女子，用柴刀把被虎叼走的父亲抢回来的事。而到了人口大爆炸的明清，随着全国各地编修地方志，加之老百姓为了种红薯和玉米拼命开荒，人虎冲突的故事也被大量记录了下来。兹举赣西永新县一例：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“（康熙五年）连月虎出四乡，死者百余人。”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“（康熙）四十一年壬午大旱，多虎伤五十余人。”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“雍正二年甲辰夏饥，禾山虎患，死者无算，至九年方绝。”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“（乾隆）九年甲子，小江山虎伤人无数，西南山被毒尤甚，至十六年方息。时虎猖撅甚，白日出田陇噬人。”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;江西只是全中国的一个缩影。事实上综观地方志及笔记，老虎在古代几乎遍布全国。如陕西汉中“虎豹成群，自沔山峡，白额恣噬”；湖南永顺“虎灾，噬人颇众”；福建安溪县因为老虎肆虐横行，“计数年之内，十八里男妇老少死于虎者不下千余人。”甚至在1846年的《新疆纪略》里，还有“老虎甚多，大路上常往来行走”这样的文字。从东北到西南，从内陆到沿海，大量老虎食人的惨剧在中国上演。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;这样的惨剧有多惨呢？我们可以看看元代陈钧的一首《猛虎行》：“荒城落日啼群乌，群乌过处草木枯。横行攫人恣为虐，闻者辟易惊相呼。白头父老道旁哭，，昨夜前村失黄犊。家惟一子赖倚依，岂料今朝葬其腹……”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148877522931301.jpg&quot; alt=&quot;商代虎食人卣，日本泉屋博物馆藏&quot; title=&quot;商代虎食人卣，日本泉屋博物馆藏&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;商代虎食人卣，日本泉屋博物馆藏&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;正如大刘在《三体》里说的那样：“生存是文明的第一需要。”在人虎矛盾如此尖锐的古代，我们不能用现在的生态观去苛求先人。古人杀老虎，说白了心态和我们现在消灭害虫没什么太大区别。然而，老虎被人大量发现并被感叹“多”的时候，也正是人类逐渐强大到无视自然法则的时候。这一点，老虎不知道，人也不知道。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;h2 data-page-model=&quot;title&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;二&lt;/span&gt;\r\n&lt;/h2&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;由于老虎异常凶猛，所以历代古人都把杀虎视为一种表现勇猛的行为。《逸周书》记载了一次周武王打猎的结果：“武王狩，禽虎二十有二，猫二，麋五千二百三十五……”将老虎排第一，可见古人对它的重视。而在春秋著名的城濮之战中，晋国为了恐吓楚军，下令将战马都蒙上虎皮，也从侧面反映了当时晋国猎杀虎数量之多。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;纵观古代的人虎之争，我们发现一个有趣的现象：不论是冯妇搏虎、李广射虎还是周处杀虎，其当事人要么是面对老虎时做出的无奈之举，要么是为了装逼而主动上阵。《殷芸小说》里有一则子路杀虎的故事，孔子认为，上等的士敢于直面凶猛的老虎，会按住虎头来杀虎；而中士的胆魄稍弱，会揪耳朵杀虎；下士则只能用抓尾巴这种方式去杀虎。杀虎还分三等，足见其逼格之高。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;而大约从宋开始，当老虎被逼得走出山林，在书本上也由“祥瑞”慢慢变成“虎患”时，人们就开始不那么讲究，而是有计划地对其实施捕杀了。一般来说，明清时地方上组织打虎的多是县一级基层政权，并没有权力调动军队，所以更多的情况是由官府出资募集猎户打虎。比较典型的是陕西西乡县的一次打虎行动：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;康熙五十一年，西乡县发生虎患，于是知县王穆悬重赏招募打虎将。当时县里下了大本钱，“得一虎辄以百镪数金赏”。猎户们自然也是摩拳擦掌，在路上安了带毒箭的机关，踩中的老虎纷纷中箭而死。再配上各种兵器和招数，三年下来，猎户们猎杀了整整六十四只老虎——这还不包括那些“中而不死，又有带铩毙于穷岩绝谷者”。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;王穆作为地方官，自然是想让这政绩万世流芳的。他得意洋洋地在城外建了“射虎亭”，还立了一块碑以记载此事。从碑文来看，王穆对自己的正义性毫不怀疑：“夫上帝好生，而生此恶兽，此造物之所不可解者也。”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;实际上，这也是当时几乎所有中国人的想法。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148877526996030.jpg&quot; alt=&quot;清代杨素《二十四孝图·扼虎救父》&quot; title=&quot;清代杨素《二十四孝图·扼虎救父》&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;清代杨素《二十四孝图·扼虎救父》&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;除了虎患，老虎的经济价值也驱使着中国人不断捕杀它。网上有个段子，说不论是什么让外国人头疼的入侵物种，只要宣传一下它能滋补壮阳，中国人立马能把它吃得渣都不剩。老虎便是这么一个倒霉的典型代表。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;就算是不懂中医的人，大概也听说过“虎骨酒”这一古老“国粹”。说来你不信，虎骨酒早在一千五百年前的《徐文伯方》中就有记载了。到了唐代，孙思邈更是在《千金要方》里记载了大量虎骨方剂，还将其细分为虎头骨、虎骨、虎屎中骨、虎骨酒方等。当然，讲实用主义的中国人是不会把目光局限在虎骨上的。小学课文里有个经典句式：“XX浑身都是宝。”就拿入药来说，四胆丸用到虎胆，催蛰丹用到虎牙，雌黄芍药圆用到虎肾。在古老的中医面前，没有什么不是“浑身都是宝”。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;那么古人吃虎肉吗？当然吃，还吃出花了。明代宋诩《宋氏养生部》中有“烹虎肉”一则，即“盐腌一日，冷水烹，稍热，易水，加花椒、葱，复烹之”。这么看来的话，似乎和内蒙古那种比较豪放的手抓羊肉吃法差不多。现在虽然老虎少了，但从这几年的新闻来看，只要有钱，想吃虎肉也不是什么太难的事。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;不少人进了老虎的肚子，而更多的老虎，则进了人的肚子。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;h2 data-page-model=&quot;title&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;三&lt;/span&gt;\r\n&lt;/h2&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;1899年9月，瑞典探险家斯文·赫定第二次来到罗布泊。在叶尔羌河边，他跟一位牧人闲聊起来：&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“在你的树林里有什么野兽？”“牡鹿、小鹿、野猪、狐狸、狼、大野猫和野兔！”“没有老虎么？”“没有，我们很久不见老虎了。”&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;后来斯文·赫定在《游移的湖》中感叹道：“亚洲万兽之王孟加拉虎过去曾出没在塔里木河中游的森林中，现在却有灭绝的危险。”事实正是如此。新疆虎作为里海虎的分支，在清代地方志里缕见记载，如今却了无踪迹。而斯氏买走的一张现存于瑞典国立民族学博物馆的虎皮，则成了新疆虎唯一存在于世的证明。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148877528971660.jpg&quot; alt=&quot;斯文·赫定《被捕机捉住的新疆虎》&quot; title=&quot;斯文·赫定《被捕机捉住的新疆虎》&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;斯文·赫定《被捕机捉住的新疆虎》&lt;/span&gt;\r\n&lt;/div&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;如果说古代的打虎还只是零散的，那么，新中国有目的有计划的打虎运动，则将中国虎逼入了绝境。就拿华南虎来说，随着社会主义建设以前所未有的广度和深度推向全国各地，大量华南虎因无食可捕而闯入人类居住区。1952年，仅湖南耒阳县就有120多人被咬死，甚至发生过一天咬死32人的事件。从某种意义上来说，这样的情况是华南虎的“回光返照”。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;华南虎伤人，换来的是解放军和民兵的协同围剿。随着打虎运动的深入，各地涌现出一批著名的“打虎英雄”。如湖南耒阳的陈曹芳，因孙子命丧虎口而组建了打虎队，七年间竟猎获华南虎138只。1957年底，他还因此受到了周恩来的接见。1959年2月，林业部颁发批示，将华南虎、熊、豹、狼等都划为有害动物，号召猎人“全力以赴地捕杀”，更是将打虎运动推向了高潮。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;据虎骨和虎皮统计，在20世纪50年代，华南虎每年的捕杀量大概是800只。还拿江西省来说，直到1983年8月8日，江西省政府办公厅与江西省军区司令部联合下文“停止消灭害兽工作”，这场长达近三十年的打虎运动才告终止。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;更令人扼腕的是，虽然国家下令停止了打虎运动，但迟迟没有采取保护措施。早在1966年，国际自然与自然资源保护联盟便在《哺乳动物红皮书》将华南虎列为濒危级，此时中国政府正在号召大规模捕杀；而到了1989年,《中华人民共和国野生动物保护法》将华南虎列入国家一级保护动物名单时，华南虎的野生种群已经消失了。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;如此一来，我们便不难理解周正龙拍下“野生华南虎”为何会引起那么大的轰动了。正如五十年代的地方政府不遗余力地灭虎一样，十年前的陕西省林业厅第一时间宣布奖励周正龙，其背后的利益指向也是不言自明的。&lt;/span&gt;\r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;汤因比说：“宇宙全体，还有其中的万物都有尊严性，它是这种意义上的存在。就是说，自然界的无生物和无机物也都有自己的尊严性。”这与庄子的“以道观之，物无贵贱”有某种暗合之处。我是贵州人。柳宗元的《黔之驴》里提到过老虎，但现在我只能在动物园里看到老虎了。以前爷爷给我讲打虎的故事，如今听来也已近乎传说。但愿人类能重视生物多样性，不要再让这些生灵只能存在于动物园，甚至是我们的记忆里。&lt;/span&gt;\r\n&lt;/p&gt;', '[\"\\u4eba\\u864e\\u77db\\u76fe\",\"\\u53e4\\u51fa\\u571f\\u7684\\u52a8\\u7269\\u5316\\u77f3\",\"\\u6bdb\\u4e3b\\u5e2d\\u7684\\u6307\\u793a\"]', '/uploads/20170306/dc2afca613f5310c1837246fcd66c563.jpg', '1488770271', '1488793038', '1');
INSERT INTO `think_document` VALUES ('13', '1', null, '0', '阔太胡静晒照搞怪卖萌 小腹隆起疑似怀二胎', '&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ent/pics/hv1/27/18/2190/142409367.jpg&quot; style=&quot;vertical-align:bottom;max-width:640px;&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;white-space:normal;background-color:#FFFFFF;font-family:宋体;font-size:10pt;&quot;&gt;\r\n	&lt;a class=&quot;nameStarQQ&quot; nameid=&quot;77919&quot; style=&quot;outline:0px;white-space:nowrap;border-bottom:1px dotted #536DA6;&quot;&gt;胡静&lt;/a&gt;微博截图\r\n&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ent/pics/hv1/35/18/2190/142409375.jpg&quot; style=&quot;vertical-align:bottom;max-width:640px;&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p align=&quot;center&quot; style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;white-space:normal;background-color:#FFFFFF;font-family:宋体;font-size:10pt;&quot;&gt;\r\n	胡静小腹隆起\r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:微软雅黑, Arial, Helvetica, sans-serif;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	腾讯娱乐讯 2月28日，胡静在微博晒出一组照片，手拿巨型项链，活力满满表情搞怪，并发文称“第一次带（注：应为“戴”）这么大项链，压的脖子疼，打算拿回家当菜板子了”，与以往姣好身材不同，照片中的胡静小腹隆起，模样似有孕，网友纷纷留言“这是牛魔王要当哥哥的节奏？”\r\n&lt;/p&gt;', '[\"dashuailiu\",\"guangzhou\",\"shenme\",\"tangxia001\"]', null, '1488774818', '1488871985', '1');
INSERT INTO `think_document` VALUES ('14', '2', 'test2', '0', '看完8小时的《如梦之梦》，剧场已是道场', '&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;17年前的千禧年，有幸为无垢剧场“花神祭”撰写所有文字创作部分，并陪同剧场舞蹈演员们排练，几乎都是高难度动作，过程非常艰辛，我做为旁观者都会跟着骨头痛。从台北国家剧院到法国里昂，一整年不间断的训练，即便不是演出时间亦照样维持状态，每场演出至少需要三组以上的演员，没有人知道谁会真的上场，即使是林丽珍老师自己。从肢体到情绪状态变量太大，为了最后在舞台上的剎那完美呈现，林老师作为创作者，不但心力强大，且具备相当的残忍，她说：“剧场是道场，没有修行的心理准备，就不该来。来了，是真爱，会愿意不顾一切地投入。”你心里只有那个完成自我的目标，在舞台上。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148878358757157.jpg&quot; alt=&quot;《如梦之梦》部分主创者&quot; title=&quot;《如梦之梦》部分主创者&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;《如梦之梦》部分主创者&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“如梦之梦”竟然也是千禧年发想的作品，赖导与夫人监制，几次不期而遇的邀约，我恰巧错过，后来演员加入了赖梵耘，亦多次邀约，终于在多年后，我撑着腰部疼痛，却精神饱满地看完八小时戏剧，原以为撑完上半场便要退席，然故事让人对下半场非常好奇。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;开场是许多人的进进出出，绕着观众席的莲花池快或慢地走着，似乎漫无目标却又有一定的方向。我们坐在旋转椅上，跟着演员们转圈圈，寻找熟悉与不熟悉的身影，耘耘搀扶着九十高龄的卢燕，像散步，又像在轮回生死之间的中阴道路上，许许多多的人，在这条路上相遇、擦身而过却视而不见，仿若任何人都与己无关，却又人人是我。如果相信轮回，这舞台上的任何一个人，都会是你，是我们任何人的某一生某一世，就在这短短几分钟步伐里，你拥有多生多世的身影。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;耘耘说：“无论是胡歌、许晴或马思纯，都甘愿放下日进斗金的机会，参与剧场演出，卢燕还一再表达感谢，有这样的机会，站在舞台上演出，好像这才是真实而有滋味的人生。”几年下来，演出多回，许晴仍台上洒泪，早早准备了面纸在固定角落，一边演出一边擤鼻涕。过瘾，是演员们的共同心声。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148878574143742.jpg&quot; alt=&quot;胡歌在《如梦之梦》剧中&quot; title=&quot;胡歌在《如梦之梦》剧中&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;胡歌在《如梦之梦》剧中&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;故事从医生世家说起，这是日据时代以来的台湾现象，最优秀的人才学医或学农，文法政不许读。台湾人普遍景仰医生，也因为他们延续了几代人的极品医术与医德，不够资优进不了医学院，没有深厚的医学世家背景，难有一席之地。然而，不管你多有才华，第一关就面对临终病人，是菜鸟医师的致命伤。你跟病人之间的距离该保持多宽？太近，心伤，太远，无济于事，医术一半取决于病患的信任。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;医生该有信仰吗？病人需要信仰吗？如果你拥有的医术不足以支撑直面病人的死亡，那么，下一步，就是哲学议题了。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;不管灵不灵，耘耘多年的佛学训练，让她站在舞台上练习自他交换呼吸法，中场休息，足足有半小时，在观众自由走动入席时，她必须维持如如不动地重复同样动作。她说：“曾经有观众居然真的跳上舞台，对着胡歌与我拍照，虽被打扰但不能出戏，只能等工作人员出手搭救。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;故事里有故事，起源于耘耘的5号病人胡歌，在濒临死亡前终于说出了自己的故事，生命中的三个女人，都曾经是别人的女人，各自有自己的故事，为了寻找顾香兰，踏上梦幻的死亡之旅。而香兰这具备上海艺妓、巴黎贵妇与艺术家多重身份的女人，也在临终前说出上个世纪的战乱传奇。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148878363667556.jpg&quot; alt=&quot;许晴在《如梦之梦》剧中&quot; title=&quot;许晴在《如梦之梦》剧中&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;许晴在《如梦之梦》剧中&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;故事并不神奇，但每个主要角色都有好几个不同年龄层的扮演者，交替着同时在舞台上出现，闪烁着时空交错的幻影，将世纪幻化为瞬间的幻灭。一段生死一场梦，不知是梦还真，好似人人都在用另一个我，看着自己的故事。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;故事里有两场怪力乱神，其一是胡歌被巴黎女友带去给吉普赛女人算命，那女人让他去找其二的镜湖，这湖可以让人看见前世今生的自己。我自己若非因缘巧合在世界各地多次遇上江湖术士，铁口直断，这两场戏，会让人觉得荒谬。不管是候鸟般到处迁徙如吉普赛人的印度沙度，还是伊斯坦堡的土耳其咖啡阅读，或者慕尼黑的塔罗牌世代女巫，甚至印度大学教授的星盘解读，都曾精准地说出了我的隐私。朋友问：“你这么容易相信吗？”我脱口而出：“不管信不信，不影响存在，我已经没有信不信的问题，而是你能够知道多少，又理解了多少？”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;由于现代剧场注重自然表达，未若早期演员们那样咬文嚼字口齿清晰，我听得很吃力，却对了解剧情毫无影响。剧名“如梦之梦”，说的是生生死死如梦似幻，而表达的是你我重叠又重叠的生命现象，让时空失去界限，可以在剎那须臾间化整为零又化零为整，如量子态。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;我在关注你的同时，其实在关注自己，如同第一场戏耘耘做的自他交换呼吸，这动作出于悲心，茫然地希冀扫除病患的痛苦，却真实地解除了自我的枷锁，如果你做自他交换是如实出于真心真意，仿若你真切地知道，这人是你也是我，随时随地。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;div class=&quot;tuzhu nofloat&quot; data-page-model=&quot;image&quot;&gt;\r\n	&lt;img src=&quot;http://img1.gtimg.com/ninja/1/2017/03/ninja148878365769742.jpg&quot; alt=&quot;《如梦之梦》剧照&quot; title=&quot;《如梦之梦》剧照&quot; /&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;《如梦之梦》剧照&lt;/span&gt; \r\n&lt;/div&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;林丽珍老师曾说，如果演员把舞台当真实人生，不但能感动观众，甚至会触动鬼神。她说这话，出于真实经历。她的经典名剧“醮”在亚维侬（Avignon）千年古城演出时，便曾遇上戏票售罄而观众席零落，但舞台上演员们看到的是满座；返台后，演员们集体病倒，到处寻找道士治疗，纷纷表示：“哪里跟来这么多洋鬼子？”后来，林老师的每场戏结束，都会集体在舞台上，以念诵心经来超度看不见的观众们。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;你可以选择不相信，这不会是我的考量，如果你不信鬼神，更好，至少不会胡乱受到惊吓。我婆婆就常说，平生不做亏心事，半夜不怕鬼敲门，正因为她真遇到往生的亲戚上门，便以这句话自我安慰，照样回床上睡大觉去了。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p data-page-model=&quot;text&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;如果我说这八小时的“如梦之梦”，正是描述生死之门的中阴身过程，你会怕吗？&lt;/span&gt; \r\n&lt;/p&gt;', '[\"\\u90ed\\u58a8\\u58a8\",\"\\u6797\\u4e3d\\u73cd\",\"\\u821e\\u53f0\\u4e0a\\u6f14\\u5458\\u4eec\"]', '/uploads/20170306/120c35e60a20a822c8ebe5fe727b1872.jpg', '1488779376', '1488854074', '1');
INSERT INTO `think_document` VALUES ('19', '1', 'test3', '0', '那些食品添加剂的谣言和误区 你知道吗？', '&lt;div class=&quot;mbArticleSharePic &quot; r=&quot;1&quot; style=&quot;position:relative;z-index:1;margin:0px auto;width:399px;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;&lt;img alt=&quot;那些食品添加剂的谣言和误区 你知道吗？&quot; src=&quot;http://img1.gtimg.com/health/pics/hv1/177/2/2191/142470462.jpg&quot; style=&quot;border-width:initial;border-style:none;vertical-align:bottom;max-width:640px;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;奶粉中掺三聚氰胺、面粉里加吊白块、用苏丹红腌鸭蛋……这些非法添加进食品中的禁用物质在坊间却一律被误认为是食品添加剂，使食品添加剂成了“背锅侠”。这已成为当前中国消费者在食品安全认知方面的最大误区之一。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;2月25日，由广东省食品药品监督管理局和有关媒体联合主办“安安有约——食药科普大讲堂活动”在广州举行第三期活动。中国工程院院士、北京工商大学副校长、北京市食品添加剂工程研究中心主任孙宝国就“辨析食品添加剂的谣言和误区”做了专题报告，为食品添加剂正名。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;食品添加剂“很受伤”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;有调查显示，80%的人认为食品安全问题是食品添加剂造成的，食品添加剂在中国可谓恶名昭著。针对这一现象，孙宝国指出：“食品添加剂是人类的伟大发明，人类使用食品添加剂历史悠久，没有食品添加剂就没有食品工业发展的今天。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;如果没有食品添加剂，“舌尖上的美味”将失色不少。孙宝国以此作了举例说明：六千年前的大汶口文化时期，酿酒用酵母中的转化酶，就是食品添加剂，是一种食用酶制剂；两千多年前，西汉淮南王刘安发明卤水点豆腐，卤水主要成分氯化镁是一种食品凝固剂，是一种食品添加剂；始于唐盛于宋的火腿制作技术使用亚硝酸盐作为防腐剂和护色剂，如果不用亚硝酸盐，火腿将出现肉毒杆菌繁殖，食用后对人体会产生极大的危害；南宋时期绍兴临安人发明了油条，所使用的明矾是食品膨化剂；红曲作为食品着色剂，在中国已有一千多年的应用历史，现代研究发现其有保健作用。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;合规使用其实很安全&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;为什么食品添加剂在中国被妖魔化？孙宝国认为，中国人的词典里以前没有食品添加剂，一些食品企业也不知道什么是食品添加剂，将非法添加物误会为食品添加剂是主因。例如把三鹿奶粉中非法加入的三聚氰胺当成食品添加剂，让食品添加剂平白无故地背了黑锅。其实，三聚氰胺是一种化工原料，不是食品原料，也不是食品添加剂，是禁止人为添加到食品中的。在奶粉中添加三聚氰胺，目的是提高奶粉中的氮含量，假冒蛋白质，属于非法掺假。此外，近些年，还有个别食品企业通过标示不含任何食品添加剂，即人们常常听说的“无添加”来误导消费者，“这其中国内外媒体传播不科学的解读也起到了推波助澜的作用，客观上也把消费者带入了误区。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;孙宝国指出，没有食品添加剂就没有现代食品工业和现代的美好生活。合理使用添加剂是有益无害的，它使食品更丰富、更安全，而滥用食品添加剂是人的过错不是食品添加剂的错。我国《食品安全国家标准 食品添加剂使用标准》（GB2760）中明确规定的食品添加剂已经过风险评估，可放心使用，但必须按照标准规定要求规范使用，滥用、超范围以及超限量使用是绝对不允许的。&lt;/span&gt; \r\n&lt;/p&gt;', '[\"azb618\",\"\\u79d1\\u8fea\\u5154\",\"\\u7206\\u7b11\\u5c0f\\u79cb\\u88e4\"]', '/uploads/20170306/4d17591d0e0634da16162365415fe526.jpg', '1488780608', '1488871985', '1');
INSERT INTO `think_document` VALUES ('20', '3', 'test1', '0', '破解“儿科医生荒”需建立人才预警机制', '&lt;div class=&quot;mbArticleSharePic &quot; r=&quot;1&quot; style=&quot;position:relative;z-index:1;margin:0px auto;width:640px;&quot;&gt;\r\n	&lt;img alt=&quot;破解“儿科医生荒”需建立人才预警机制&quot; src=&quot;http://img1.gtimg.com/health/pics/hv1/107/37/2191/142479317.jpg&quot; style=&quot;border-width:initial;border-style:none;vertical-align:bottom;max-width:640px;&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;全面两孩政策实施后，“儿科医生紧缺”成为社会关注焦点。全国政协委员、苏州大学校长熊思东在接受记者采访时表示，解决问题的根源应从“医疗供给侧改革”入手，建立卫生人才需求预警机制。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;图为熊思东委员在驻地办公。人民网王宇鹏 摄&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“目前95%以上二级医院儿科医生不足。”熊思东坦言，“实际上，紧缺的不仅仅是儿科医生，我们推进分级诊疗，基层就需要大量全科医生；发展医养结合，养老院就需要大量医护人员；公立医院取消药品加成，未来如果药房从医院中剥离，药店便需要大量药剂师……”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;在他看来，国家即便紧急扩大培养规模，仍然远水救不了近火。医药卫生人才培养周期较长，除了至少5年的本科教育时间，从本科毕业到成长为一名有经验的成熟的医生，还需要5-10年的临床经验积累。因此，相较于紧迫的人才需求，医药卫生人才培养具有滞后性。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“解决目前医疗人才的供需矛盾，需要顶层设计和全面规划，规划的起点在哪里？需要有预警机制。通过这一机制提高人才需求的预见性，以便提前部署、调整医学教育招生规模与结构，保证我国医疗卫生事业的可持续发展。”熊思东说。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;为此，他提出三点建议：&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;一是融合互联网和大数据技术，开展医药卫生人才现状和需求调查。利用互联网技术，建立起覆盖省、市（地）、县（市、区）三级并逐步延伸到社区、乡村的医药卫生人才调查网络。同时，结合大数据手段，全面分析掌握不同地区、不同层级医院医药卫生人才的年龄结构、质量结构和专业结构现状，以及各地区医药卫生人才需求结构。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;二是结合“全面两孩”、城镇化等国家重大政策调整和经济社会发展新常态，加强医药卫生人才需求前瞻性预测。例如，“全面两孩”政策的落地，必将导致妇产科、儿科医生需求的急剧增长及其它社会需求的变化。医药卫生人才的培养不是一蹴而就的事，具有5-10年的滞后期。因此，在政策落地前，需要对政策将带来的医药卫生人才的需求变化开展前瞻性预测，提前部署调整妇产科、儿科医生的培养规模。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:29px;padding:0px;line-height:28px;font-family:宋体, Arial, sans-serif;font-size:15.9996px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;三是加强部门之间的协同，保证信息的畅通。建议国家卫生和计划生育委员会、教育部、人社部成立联合工作组，及时会商，定期发布医疗卫生人才需求预测信息，指导高校开展专业设置和招生工作。&lt;/span&gt; \r\n&lt;/p&gt;', '[\"\\u8ffd\\u68a6\\u81ea\\u7136\",\"\\u5361\\u62c9\\u8d44\\u8baf\\u8054\\u76df\",\"\\u5e7d\\u9ed8\\u563b\\u54c8\\u94fa\"]', '/uploads/20170306/4ee900f440dccdaa80a4e1fd899c0088.jpg', '1488780767', '1488859761', '1');
INSERT INTO `think_document` VALUES ('21', '3', 'test3', '0', ' 新一年，这些民生方面的政策改善值得期待', '&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;&quot; align=&quot;center&quot;&gt;\r\n	&lt;img alt=&quot;新一年，这些民生方面的政策改善值得期待&quot; src=&quot;http://img1.gtimg.com/health/pics/hv1/8/90/2191/142492733.jpg&quot; style=&quot;vertical-align:middle;max-width:640px;&quot; /&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“民生是为政之要，必须时刻放在心头、扛在肩上。”3月5日，总理在十二届全国人大五次会议上所作的政府工作报告掷地有声，表达了深厚的民生关怀，赢得了会场内的热烈掌声和会外群众的热情点赞。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;今年再减少农村贫困人口1000万以上，再完成棚户区住房改造600万套，城乡居民医保财政补助由每人每年420元提高到450元，年内全部取消手机国内长途漫游费等一系列改善民生的政策礼包，让全面建成小康社会的目标更加切实可感，让老百姓对未来的美好生活满怀期待。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;精准扶贫，提高农民收入&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“小康不小康，关键看老乡。近年来，国家不断加大扶贫力度，推进农村发展、农民增收，这是为全面建成小康社会补齐最大的短板。”河北省承德市滦平县路南营村第一书记杨远帆在接受本报采访时说。今年政府工作报告明确提出再减少农村贫困人口1000万以上，中央财政专项扶贫资金增长30%以上，体现了脱贫攻坚的决心，让贫困地区的老乡们备受鼓舞。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“中央一号文件和政府工作报告都强调要促进农民增收，这确实说出了我们农民心底最大的期望。”湖南省新化县水车镇老庄村党支部书记罗洗感叹道。在他看来，扶贫应当因地制宜，鼓励发展符合当地优势的特色产业。“我们村地处紫鹊界梯田景区，具有打造采摘体验园的条件，却苦于没有项目启动资金，希望政府能提供相应的支持。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;在滦平县路南营村，通过农业产业园“一地生四金”的扶贫模式，农民收入实现显著增长。到2016年底，路南营村270户一般贫困户已经全部摘帽。“摘帽不摘政策，村民们将继续享受扶贫政策支持。”杨远帆说，“今年将重点开展美丽乡村建设，由政府出资帮助改善厨房、厕所等生活设施，兴建公园、文化广场和水景观等，拓展休闲旅游产业链，让村民们生活条件更好，收入进一步提高。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;深化医改，建设健康中国&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;家住河北唐山的王奶奶，对于医保政策格外关注：“听说医保财政补助从420元提高到450元，我当然高兴啦。孩子在北京工作，我打算到北京养老，希望医保异地结算能早日实现。”两年前，由于医保信息没有联网，王奶奶在北京住院就医不能通过医保报销。让她感到欣慰的是，今年政府工作报告提出在全国推进医保信息联网，实现异地就医住院费用直接结算。“以后我在北京的医院也能刷唐山的社保卡，那就太方便了！”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;事实上，医疗方面的改革红利还不只这些。政府工作报告要求“推进健康中国建设”，全面推开公立医院综合改革，全部取消药品加成，全面启动多种形式的医疗联合体建设试点，分级诊疗试点和家庭签约服务扩大到85%以上地市……这一套改革“组合拳”，将有效缓解“看病难、看病贵”的问题。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“由于县级医院条件不足，之前我母亲做关节镜手术要跑到福州市级医院去。看感冒这种小病，有时候医生还会开一些很贵的进口药。”福建宁德的林先生表示，随着医改深化落实，基层医疗服务水平不断提高，就近就医将更加便利，“以药养医”的怪象也有望破解。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;促进就业，用劳动创造财富&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;今年城镇新增就业预期目标1100万人以上，比去年多100万人，突出了更加重视就业的导向。总理在政府工作报告中提出，必须牢牢抓住就业这一民生之本，让人们在劳动中创造财富，在奋斗中实现人生价值。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;“一看今年的高校毕业生有795万人，又创历史新高，我其实是有就业压力的。但是连续几年来，就业目标都超额完成，今年提出了更高的目标，表明政府有信心实现。”大连海事大学的姜同学说。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;新产业新业态的发展，大众创业万众创新的活跃，是就业增长再创新高的底气所在。“政府工作报告说要加强对创新型中小微企业支持，打造面向大众的‘双创’全程服务体系，这是我们非常期待的。”兔巴电子产品工作室创始人兼首席执行官、中国人民大学2017届毕业生徐思源对本报表示，“希望政府为大学生创业提供法务、财务方面的专业培训指导，细化落实创业支持政策，同时进一步简化办事创业的手续。”&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;strong&gt;&lt;span style=&quot;font-size:16px;&quot;&gt;铁腕治污，打好蓝天保卫战&lt;/span&gt;&lt;/strong&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;在北京工作一年多的郑女士亲身体会到雾霾问题的严重。她希望今年能多看见一些蓝天，少一些“会呼吸的痛”。专家表示，今年政府工作报告在生态环境保护治理方面措辞力度空前，彰显了铁腕治污的决心，回应了社会的强烈关切。报告还提出要严格环境执法和督查问责，对偷排、造假的严厉打击，对执法不力、姑息纵容的严肃追究，对空气质量恶化、应对不力的严格问责。&lt;/span&gt; \r\n&lt;/p&gt;\r\n&lt;p style=&quot;margin-top:0px;margin-bottom:28px;padding:0px;font-family:&amp;quot;font-size:16px;white-space:normal;background-color:#FFFFFF;text-indent:2em;&quot;&gt;\r\n	&lt;span style=&quot;font-size:16px;&quot;&gt;国务院发展研究中心资源与环境政策研究所副所长常纪文认为，经过几年的分析、研究，政府和学界对于雾霾污染的成因基本上清楚了，如今治理措施力度越来越大，针对性也日益加强，只要坚持执行落实下去&lt;/span&gt; \r\n&lt;/p&gt;', '[\"GIF\\u5feb\\u624b\\u7c89\\u4e1d\",\"\\u8863\\u9970\\u6709\\u4f60\",\"\\u7f8e\\u5471\\u751f\\u6d3b\\u9986\"]', '/uploads/20170306/4bd77900e09e0afeaf94eb1f5cb179d9.jpg', '1488780785', '1488859751', '1');
INSERT INTO `think_document` VALUES ('24', '2', 'test1', '0', '人大代表：建议延长春节假期至15天', '&lt;div class=&quot;img_wrapper&quot;&gt;\r\n	&lt;img style=&quot;max-width:500px;&quot; src=&quot;http://n.sinaimg.cn/translate/20170305/Yh7S-fycapec1797552.jpg&quot; /&gt; \r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;春节是我国民间最隆重、最热闹的传统节日，正月初一春节至正月十五元宵节，每一个日子都充满着中国传统文化元素。在这一最重要的节日里，“回家过年”成为每个国民最朴实、最期盼实现的愿望。然而这样的一个愿望却因为假期短、难买票等因素受到影响。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;为此，十二届全国人大代表、中国华融资产管理股份有限公司董事长赖小民提出建议，“将春节法定假期由7天延长为15天，即假期由除夕延至元宵节”。对于部分运营单位或公共服务单位错峰休假，保证春节期间人民生活的正常运转。提高假期期间企业选择生产经营时间的自主权，但要保证员工利益不受损失，以调休或补贴的方式进行补偿。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;赖小民表示，近年来，铁路、民航等运输部门不断加强交通运力和管理手段等各方面工作，并取得了较好效果，但春节期间买票难仍是许多人过年回家旅程中挥之不去的愁。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;据交通运输部统计，2017年春节假期全国旅客发送量4.08亿人次，比去年同期增长2.1%，其中铁路5199万人次、增长8.8%，道路3.36亿人次、增长0.8%，水路1035万人次、增长3.5%，民航982万人次、增长14.9%。春节已成为世界上最大的周期性运输高峰，这种短期内大规模的人口流动对我国交通运输形成较大挑战，也降低了人们出行的舒适度。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;此外，&amp;nbsp;由于春节假期较短，大量人口集中出行，对交通资源、旅游资源和公共服务体系形成巨大挑战，超出资源对应的合理人口规模，安全隐患大幅增加。年年春运提安全问题，但每年春节期间都是安全事故的多发期，重大道路交通事故、重大船舶翻沉事故、重大渔船事故时有发生。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;赖小民代表也发现，春节七天假期除去奔波往返的时间，实际假期很短，尤其是对于家在外地、路途遥远的上班族来说春节假期尤显不足，导致很多人无法感受传统年文化的氛围以及与家人团聚的温暖。\r\n&lt;/p&gt;\r\n&lt;div id=&quot;ad_44086&quot; class=&quot;otherContent_01&quot; style=&quot;display:block;margin:10px 20px 10px 0px;float:left;overflow:hidden;clear:both;padding:4px;width:300px;height:250px;&quot;&gt;\r\n	&lt;ins class=&quot;sinaads sinaads-done&quot; id=&quot;Sinads49447&quot; data-ad-pdps=&quot;PDPS000000044086&quot; data-ad-status=&quot;done&quot; style=&quot;display:block;overflow:hidden;text-decoration:none;&quot;&gt;\r\n		&lt;ins style=&quot;text-decoration:none;margin:0px auto;width:300px;display:block;position:relative;overflow:hidden;&quot;&gt;\r\n		&lt;/ins&gt;\r\n	&lt;/ins&gt;\r\n&lt;/div&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;据统计，目前我国有900多万留守儿童、5000多万留守老人，平日里因父母或子女长期在外工作见面次数极少。赖小民代表表示，春节回家又由于假期较短，还未好好交流就要匆匆道别，造成与夫妻、子女、父母、祖孙之间的生疏，最终导致年味淡了、人情薄了、关系疏远了，作为社会的基本细胞，同根同源意识和家庭亲情观念淡薄将会造成传统优良美德的丧失，最终影响到和谐社会的构建和社会主义核心价值观的体现。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;在赖小民看来，春节承载着厚重的优秀传统文化和历史积淀， 是亿万中国人情感的聚合， 在中国人心目中有着神圣的地位，适当延长假期时间可以进一步促进人们传承、保护和创新中国传统文化。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;新华网曾对是否延长春节假期做过调查，95.49%的网民支持延长春节假期，其中，69.84%的网民支持春节假期为15天，20.99%的网民支持春节假期为10天。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n	&amp;emsp;&amp;emsp;因此，赖小民代表建议，结合传统风俗、现实需要和国民诉求适当延长春节假期。同时，春节是全民消费的节日，衣食住行都能增加消费、活跃经济。据商务部统计，2017年春节假期全国零售和餐饮企业实现销售额约8400亿元，同比增长11.4%。\r\n&lt;/p&gt;\r\n&lt;span style=&quot;font-size:16px;&quot;&gt;&lt;/span&gt;', '[\"01\",\"02\",\"03\"]', '/uploads/20170306/2a12f1e7e2c56599e0d5e9331f1c9c96.jpg', '1488800045', '1488854176', '1');
INSERT INTO `think_document` VALUES ('25', '2', null, '0', '韩媒疯狂攻击中国！乐天辟谣：对中国始终怀有深深情义', '&lt;strong&gt;\r\n&lt;p style=&quot;text-align:center;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;&lt;img src=&quot;http://himg2.huanqiu.com/attachment2010/2017/0307/03/33/20170307033333528.png&quot; style=&quot;width:448px;height:630px;&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:center;font-size:16px;&quot;&gt;\r\n	乐天官方声明为萨德“换地”行为辩解。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;【环球时报驻&lt;a href=&quot;http://country.huanqiu.com/korea&quot; class=&quot;linkAbout&quot; target=&quot;_blank&quot; title=&quot;韩国&quot;&gt;韩国&lt;/a&gt;特约记者 济冬 环球时报记者 谷棣 俞佳兰 金惠真】“乐天&lt;a href=&quot;http://country.huanqiu.com/china&quot; class=&quot;linkAbout&quot; target=&quot;_blank&quot; title=&quot;中国&quot;&gt;中国&lt;/a&gt;更多门店被勒令停业”——韩国《&lt;a href=&quot;http://country.huanqiu.com/north_korea&quot; class=&quot;linkAbout&quot; target=&quot;_blank&quot; title=&quot;朝鲜&quot;&gt;朝鲜&lt;/a&gt;日报》6日称，据零售业界和乐天集团人士透露，截至北京时间6日下午4时，被勒令停止营业的在华乐天玛特数量增加至23家。报道称，考虑到在华乐天玛特总共99家，等于约1/4的店关门停业。韩国零售业界人士担忧“未来被勒令停业的店面可能进一步增加”。6日，乐天(中国)企业管理有限公司在发给《环球时报》的声明中，痛斥中国网络上近日疯传的“乐天集团会长辛东彬称中国人非常市侩，无骨气无血性”为假新闻。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;6日一早，路透社引述乐天集团发言人的话称，在中国境内的4家乐天玛特超市接受当地官员检查后被关闭。6日中午，韩联社将这一数字增加到15家。下午，《朝鲜日报》称23家在华乐天玛特被停业。报道称，后来被停业的门店主要集中在江苏、安徽和浙江等地。乐天玛特进军中国市场十几年来，虽然有过几次被中国政府相关部门处以罚款的经历，但被勒令停业还属首次。路透社称，乐天卖场的发言人没有提供更详细的停业信息，但是有工作人员透露，停业是暂时的，与卖场的防火安全有关。其中一家门店预计将在数周内重开，另外一家则将在4月1日重开。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;韩国媒体对此不以为然。“乐天从中国撤出，不啻为一个转危为安的契机”，韩国《亚细亚经济》6日以此为题称，韩国Hi投资证券分析师李尚贤(音)认为，预计未来将有更多的乐天玛特相继遭遇停业命运。“不过，如果乐天因不堪中国当局的进一步报复而选择从中国市场撤出，不啻为一个转危为安的契机。”他称，像乐天购物这样的零售业公司在华拥有约120家店面(5家百货店、99家玛特、16家超市)，但去年乐天百货店和乐天玛特的海外营业损失额分别高达830亿韩元和240亿韩元，其中80%—90%损失发生在中国市场。“因此，如果乐天零售业从中国撤出，业绩反而会得到改善。”\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;韩国政府决定部署“萨德”引发中国民众反对，为“萨德”提供用地的乐天集团也成为部分中国人抗议的目标，一些假新闻随之滋生。近日流传在中国网络的一则消息称，韩国的《环球新闻眼》采访乐天集团会长辛东彬，当记者问及当前中国人抵制乐天，乐天集团有何打算时，辛东彬面露笑容说：“不用担心，中国人非常市侩，无骨气无血性，我们降价他们就买。我对乐天在中国市场的前景非常乐观。根据以往的经验，他们最多抵制一段时间，像刮阵风。”\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;“这是无中生有、捏造事实、违背常理、荒诞不经的虚假信息”，乐天(中国)6日发给《环球时报》的声明中一口否认了该内容。声明说，韩国根本不存在《环球新闻眼》这个新闻媒体，并且乐天集团没有接受过任何媒体关于“萨德”与乐天在华业务的采访。声明还称，由于韩国政治政策上决定，乐天作为企业实际上没有选择。乐天对中国始终怀有深深的情义，近年来根据中国政府的政策引导，在开发西部和振兴东北方面进行了巨额的投资，在投资还未取得盈利的情况下，近几年公益投入超千万元。声明最后称，我们理解目前情况，但对这次虚假信息的传播非常忧虑，将会向有关机关申请管制和协助&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;6日，韩国保守派媒体对中国展开疯狂攻击。《中央日报》6日发表社评称，中国在旅游、乐天方面的行动明显是报复“萨德”入韩，意味着人们很难期待中国恪守所谓的法制或者国际贸易准则。“现在的中国已经成为韩半岛、东亚地区，乃至全世界的危险分子。”文章称，&lt;a href=&quot;http://country.huanqiu.com/america&quot; class=&quot;linkAbout&quot; target=&quot;_blank&quot; title=&quot;美国&quot;&gt;美国&lt;/a&gt;国务卿蒂勒森计划3月20日前后接连访问韩日中三国。文章称，“蒂勒森应要求中国停止报复行为，并集中一切外交力量，谋求在萨德问题上得到中国谅解。”\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;“中方心胸狭隘的萨德报复，我们足够扛得住”，《首尔新闻》6日社评以此为题称，在华经营的乐天购物店雇用的当地员工达2万人，此外，2.5万家在华韩企雇用的当地员工数也达到数百万。因此不必过度担心中国心胸狭隘的报复举措。文章称，韩国经济规模排在世界20位以内，有足够的实力扛得住中国的报复行动。“要知道，韩国可是先后经历过1998年国际货币危机以及2008年世界金融危机的。”该社评称。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;font-size:16px;&quot;&gt;\r\n	&amp;emsp;&amp;emsp;据韩国CBS网站6日报道，济州道知事元喜龙当天召开紧急会议，商讨如何应对中国“禁止赴韩旅游”措施。会上，元喜龙抨击中国政府“为达到政治、军事目的而动员经济报复手段，不符合大国应有的气度，也不符合世界级领导国家应有的格调。”\r\n&lt;/p&gt;\r\n&amp;emsp;&amp;emsp;中国国际问题研究院研究员杨希雨6日对《环球时报》表示，朝鲜半岛每平方公里部署的军事武器密度堪称世界第一，韩媒还诬称别人是“危险分子”，简直是倒打一耙，赤裸裸地不讲道理。韩国保守派的观点就是，凡是有周边大国反对自己的决策，就本能地跳起来说“以大欺小”。蒂勒森东亚行无非是要解释“萨德”不针对中国，但无法消除中方的疑问：“萨德”连韩国人口最稠密的首尔都覆盖不了，却能“反刺”中国上千公里，韩国引入“萨德”究竟意欲何为？&lt;/strong&gt;', '[\"bxt110\",\"bxt2205\",\"bxt45665\"]', '/uploads/20170307/bf34b1dd27a0bc2fa0fdfbd5e8cc2045.png', '1488857457', '1488871985', '1');
INSERT INTO `think_document` VALUES ('26', '1', 'test2', '0', '老公现在每晚开启洪荒之力，原来秘密在这里', '&lt;div id=&quot;js_article&quot; class=&quot;rich_media&quot; style=&quot;margin:0px;padding:0px;font-family:-apple-system-font, &amp;quot;font-size:medium;white-space:normal;&quot;&gt;\r\n	&lt;div class=&quot;rich_media_inner&quot; style=&quot;margin:0px;padding:0px;font-size:16px;&quot;&gt;\r\n		&lt;div id=&quot;page-content&quot; style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n			&lt;div id=&quot;img-content&quot; class=&quot;rich_media_area_primary&quot; style=&quot;margin:0px;padding:20px 15px 15px;position:relative;background-color:#FFFFFF;&quot;&gt;\r\n				&lt;h2 class=&quot;rich_media_title&quot; id=&quot;activity-name&quot; style=&quot;margin:0px 0px 10px;padding:0px;font-weight:400;font-size:24px;line-height:1.4;&quot;&gt;\r\n					&lt;div id=&quot;js_article&quot; class=&quot;rich_media&quot; style=&quot;margin:0px;padding:0px;font-family:-apple-system-font, &amp;quot;font-size:medium;white-space:normal;&quot;&gt;\r\n						&lt;div class=&quot;rich_media_inner&quot; style=&quot;margin:0px;padding:0px;font-size:16px;&quot;&gt;\r\n							&lt;div id=&quot;page-content&quot; style=&quot;margin:0px;padding:0px;&quot;&gt;\r\n								&lt;div id=&quot;img-content&quot; class=&quot;rich_media_area_primary&quot; style=&quot;margin:0px;padding:20px 15px 15px;position:relative;background-color:#FFFFFF;&quot;&gt;\r\n									&lt;h2 class=&quot;rich_media_title&quot; id=&quot;activity-name&quot; style=&quot;margin:0px 0px 10px;padding:0px;font-weight:400;font-size:24px;line-height:1.4;&quot;&gt;\r\n										老公现在每晚开启洪荒之力，原来秘密在这里...\r\n									&lt;/h2&gt;\r\n									&lt;div class=&quot;rich_media_meta_list&quot; style=&quot;margin:0px 0px 18px;padding:0px;line-height:20px;font-size:0px;&quot;&gt;\r\n									&lt;/div&gt;\r\n									&lt;div class=&quot;rich_media_content &quot; id=&quot;js_content&quot; style=&quot;margin:0px;padding:0px;overflow:hidden;color:#3E3E3E;&quot;&gt;\r\n	