INSERT INTO `think_products` VALUES ('1', '1', '百姓堂虾条', '<p>嘿嘿虾<img src=\"http://tp5.tpadmin.mmt//uploads/image/20170413/1492076544123255.jpg\" title=\"1492076544123255.jpg\" alt=\"1.jpg\"/></p>', '龙虾', '/uploads/20170416/391d11ee1caf10a03d447791c5260eb9.jpg', '1', '0', '2017-04-13 17:42:06', '2017-04-13 17:42:06', '皮皮虾');
INSERT INTO `think_products` VALUES ('2', '1', '资质标题', '<p>搜索<br/></p>', '龙猫', '/uploads/20170413/fbdd65d8ca3857e957d7e1c217fe5f16.jpg', '1', '0', '2017-04-13 17:49:46', '2017-04-13 18:43:35', '搜索');
INSERT INTO `think_products` VALUES ('19', '1', '中医泰斗', '<p style=\"text-align: left;float:left\"><img src=\"http://tp5.tpadmin.mmt//uploads/image/20170416/1492328121326525.jpg\" title=\"1492328121326525.jpg\" alt=\"xxHU-fxtwihp9798671.jpg\"/></p><p style=\"text-align: left; text-indent: 2em;\"><span style=\"font-size: 14px;\">在北京的中医界，有位德高望重的老先生，95岁仍在治病救人，</span></p><p style=\"text-align: left; text-indent: 2em;\"><span style=\"font-size: 14px;\">是目前北京最年长的出诊大夫。他就是国医大师路志正。</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">记者在诊所看到，路志正大夫正认真地给病人号脉、写病历、</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">开药，只见他面色红润、气定神闲，写字时手一点都不抖。</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">路老现在每周出三个半天的门诊，还要经常出席各种会议，</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">工作非常繁忙。如此高龄还能承担这样大的工作量，</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">让人惊叹不已。路老告诉《生命时报》记者，</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">他有一个坚持了40多年的习惯，那就是吃姜。</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">工作非常繁忙。如此高龄还能承担这样大的工作量，</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">让人惊叹不已。路老告诉《生命时报》记者，</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">他有一个坚持了40多年的习惯，那就是吃姜。</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">工作非常繁忙。如此高龄还能承担这样大的工作量，</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">让人惊叹不已。路老告诉《生命时报》记者，</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\">他有一个坚持了40多年的习惯，那就是吃姜。</span></p><p style=\"text-indent: 2em; text-align: left;\"><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-indent: 2em;