INSERT INTO `think_video` VALUES ('21', 'hc - 悄悄的离开', '/uploads/image/20170530\\efba3f2759cdb9bc0031b6cc3f98f78f.png', '/uploads/video/20170530\\00852305c901511456615194cf664a49.mp4', '0', '0', '0', '0', '0000-00-00 00:00:00', '0000-00-00 00:00:00');
