INSERT INTO `think_weditor` VALUES ('113', '8', '&lt;section class=&quot;wxqq-borderTopColor wxqq-borderRightColor wxqq-borderBottomColor wxqq-borderLeftColor&quot; style=&quot;border:none;border-style:none;overflow: hidden;margin: 1em auto;width: 100%;text-align:center;border:solid 1px #000000;&quot; id=&quot;96weixinduanwu3&quot;&gt;&lt;section style=&quot;padding:20px 20px 49px 20px;min-height: 200px;font-size: 15px;line-height:1.8;&quot;&gt;《乙卯重五诗》宋: 陆游，重五山村好，榴花忽已繁。粽包分两髻，艾束著危冠。旧俗方储药，羸躯亦点丹。日斜吾事毕，一笑向杯盘。&lt;/section&gt;&lt;img src=&quot;/static/images/weditor/066.gif&quot; style=&quot;width:110px;float:right;margin-right: 20px;margin-top:-50px; &quot;/&gt;&lt;/section&gt;&lt;p&gt; \r\n									&lt;/p&gt;', '1494640401', '0');
