INSERT INTO `think_weditor` VALUES ('115', '8', '&lt;section label=&quot;wxqq-borderTopColor&quot; id=&quot;96weixinduanwu6&quot; style=&quot;border-style:none;border: none rgb(0, 0, 0); margin: 2em 0px; padding-bottom: 1.5em; font-size: 14px; font-weight: bold; text-align: center; text-decoration: inherit;&quot;&gt;&lt;img src=&quot;/static/images/weditor/068.gif&quot; style=&quot;color:inherit; width:76px; margin-right:15px; margin-top: 0px&quot;/&gt;&lt;section class=&quot;wxqq-borderTopColor wxqq-borderRightColor wxqq-borderBottomColor wxqq-borderLeftColor&quot; style=&quot;border-left-style: solid; border-left-width: 2px; border-left-color: rgb(0, 0, 0); padding-left: 15px; padding-right: 1em; text-align: left; display: inline-block; height: 3.7em; line-height: 3.7em; vertical-align: top;&quot;&gt;&lt;section style=&quot;width: 100%; overflow: hidden; margin-bottom: -1px;font-size: 24px;&quot;&gt;端午节&lt;/section&gt;&lt;/section&gt;&lt;/section&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;									&lt;/p&gt;', '1494640424', '0');
INSERT INTO `think_weditor` VALUES ('116', '8', '&lt;section label=&quot;wxqq-borderTopColor&quot; style=&quot;border: none; width: 100%; margin: 1em auto; background-image: none; &quot; id=&quot;96weixinduanwu7&quot;&gt;&lt;span class=&quot;wxqq-borderTopColor wxqq-borderRightColor wxqq-borderBottomColor wxqq-borderLeftColor&quot; style=&quot;display: block;border: solid 1px #527c03;padding:20px 20px 20px 20px;position:relative;line-height:1.4;margin-top: 27px;&quot;&gt;&lt;section style=&quot;float: left;margin-top: -39px;margin-left: -4px;margin-bottom:-20px;background-color: #ffffff;min-width: 90px;padding: 0px 20px 5px 3px;&quot;&gt;&lt;img src=&quot;/static/images/weditor/069.gif&quot; style=&quot;width:39px;margin: 2px 5px;vertical-align: middle;&quot;/&gt;端午节民俗活动&lt;/section&gt;&lt;section&gt;过端午节，是中国人二千多年来的传统习惯，由于地域广大，民族众多，加上许多故事传说，于是不仅产生了众多相异的节名，而且各地也有着不尽相同的习俗。其内容主要有：女儿回娘家，挂钟馗像，迎鬼船、躲午，帖午叶符，悬挂菖蒲、艾草，游百病，佩香囊，备牲醴,赛龙舟，比武，击球，荡秋千，给小孩涂雄黄，饮用雄黄酒、菖蒲酒，吃五毒饼、咸蛋、粽子和时令鲜果等。&lt;/section&gt;&lt;/span&gt;&lt;/section&gt;&lt;p&gt;&amp;nbsp;									&lt;/p&gt;', '1494640438', '0');
INSERT INTO `think_weditor` VALUES ('117', '8', '&lt;section data-id=&quot;44390&quot; class=&quot;wxqq-borderBottomColor&quot; style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; color: rgb(62, 62, 62); white-space: normal; border: 0px none; font-size: 14px; font-family: 微软雅黑; background-color: rgb(255, 255, 255); word-wrap: break-word !important;&quot;&gt;&lt;section data-bcless=&quot;darken&quot; style=&quot;margin: 10px auto; padding: 0px; max-width: 100%; box-sizing: border-box; border: 1px solid rgb(197, 200, 204); text-align: center; border-top-left-radius: 5px; border-top-right-radius: 5px; border-bottom-right-radius: 5px; border-bottom-left-radius: 5px; box-shadow: rgb(255, 255, 255) 0px 1px 0px, rgb(197, 200, 204) 0px 2px 0px, rgb(255, 255, 255) 0px 3px 0px, rgb(197, 200, 204) 0px 4px 0px; word-wrap: break-word !important;&quot;&gt;&lt;section class=&quot;wxqq-bg&quot; style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; height: 25px; border-bottom-width: 0px; border-bottom-style: solid; border-bottom-color: rgb(197, 200, 204); border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-right-radius: 0px; border-bottom-left-radius: 0px; background-color: rgb(219, 219, 221); word-wrap: break-word !important;&quot;&gt;&lt;section data-width=&quot;14px&quot; style=&quot;margin: 0px 100px 0px 0px; padding: 0px; max-width: 100%; box-sizing: border-box; display: inline-block; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(173, 173, 173); word-wrap: break-word !important;&quot;&gt;&lt;section data-width=&quot;14px&quot; style=&quot;margin: 30px 0px 0px; padding: 0px; max-width: 100%; box-sizing: border-box; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(222, 222, 222); word-wrap: break-word !important;&quot;&gt;&lt;/section&gt;&lt;section data-width=&quot;8px&quot; style=&quot;margin: -37px 0px 0px 2px; padding: 0px; max-width: 100%; box-sizing: border-box; width: 8px; height: 30px; border: 1px solid rgb(202, 202, 202); border-top-left-radius: 3px; border-top-right-radius: 3px; border-bottom-right-radius: 3px; border-bottom-left-radius: 3px; background-color: rgb(255, 255, 255); word-wrap: break-word !important;&quot;&gt;&lt;/section&gt;&lt;/section&gt;&lt;section data-width=&quot;14px&quot; style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; display: inline-block; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(173, 173, 173); word-wrap: break-word !important;&quot;&gt;&lt;section data-width=&quot;14px&quot; style=&quot;margin: 30px 0px 0px; padding: 0px; max-width: 100%; box-sizing: border-box; height: 14px; width: 14px; border-top-left-radius: 7px; border-top-right-radius: 7px; border-bottom-right-radius: 7px; border-bottom-left-radius: 7px; background-color: rgb(222, 222, 222); word-wrap: break-word !important;&quot;&gt;&lt;/section&gt;&lt;section data-width=&quot;8px&quot; style=&quot;margin: -37px 0px 0px 2px; padding: 0px; max-width: 100%; box-sizing: border-box; width: 8px; height: 30px; border: 1px solid rgb(202, 202, 202); border-top-left-radius: 3px; border-top-right-radius: 3px; border-bottom-right-radius: 3px; border-bottom-left-radius: 3px; background-color: rgb(255, 255, 255); word-wrap: break-word !important;&quot;&gt;&lt;/section&gt;&lt;/section&gt;&lt;/section&gt;&lt;section class=&quot;135brush&quot; style=&quot;margin: 40px 10px; padding: 0px; max-width: 100%; box-sizing: border-box; word-wrap: break-word !important;&quot;&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; min-height: 1em; white-space: pre-wrap; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(151, 72, 6); font-size: 16px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;父亲节&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; font-family: arial, 宋体, sans-serif; line-height: 24px; text-indent: 28px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;Father&amp;#39;s Day&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; max-width: 100%; box-sizing: border-box; min-height: 1em; white-space: pre-wrap; word-wrap: break-word !important;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(0, 0, 0); font-family: arial, 宋体, sans-serif; line-height: 24px; text-indent: 28px; box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;顾名思义是感恩父亲的节日。约始于二十世纪初，起源于美国，现已广泛流传于世界各地，节日日期因地域而存在差异。最广泛的日期在每年&lt;span style=&quot;margin: 0px; padding: 0px; max-width: 100%; color: rgb(151, 72, 6); box-sizing: border-box !important; word-wrap: break-word !important;&quot;&gt;6月的第三个星期日&lt;/span&gt;，世界上有52个国家和地区是在这一天过父亲节。节日里有各种的庆祝方式，大部分都与赠送礼物、家族聚餐或活动有关。&lt;/span&gt;&lt;/p&gt;&lt;/section&gt;&lt;/section&gt;&lt;/section&gt;&lt;p&gt;&amp;nbsp;										&lt;/p&gt;', '1494640450', '0');
