INSERT INTO `think_weditor` VALUES ('125', '8', '&lt;section label=&quot;wxqq-borderTopColor&quot; style=&quot;margin: 1em auto; width: 100%; border: 1px solid rgb(196, 180, 141); border-top-left-radius: 3px; border-top-right-radius: 3px; border-bottom-right-radius: 3px; border-bottom-left-radius: 3px;&quot; id=&quot;96weixinfuqin4&quot;&gt;&lt;span style=&quot;width: 100%;min-height: 169px;padding:14px;color: #666666;display: inline-block;&quot;&gt;&lt;img src=&quot;/static/images/weditor/073.gif&quot; style=&quot;width: 130px;margin-right: 8px;float:left;&quot; alt=&quot;&quot;/&gt;&lt;section style=&quot;width:90%;line-height: 1.6;&quot;&gt;总是向你索取，却不曾说谢谢你，直到长大以后，才懂得你不容易&lt;/section&gt;&lt;/span&gt;&lt;/section&gt;&lt;p&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;									&lt;/p&gt;', '1494640555', '0');
